// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {IDataBridge} from "../../../Core/interfaces/internal/IDataBridge.sol";
import {QuoteParams} from "../../../Core/structs/QuoteParams.sol";
import {FixedPoint64x64} from "../../libraries/FixedPoint64x64.sol";

/// @title StoixDataBridge (consolidated real bridges)
/// @notice Single external call returning packed per-flag payloads. Formatting at bridge level (Q64.64).
contract StoixDataBridge is IDataBridge {
    using FixedPoint64x64 for uint128;

    // Function flags (lower 8 bits after the "use Stoix Bridge" bit)
    // bit0: UniV2 spot, bit1: UniV3 spot, bit2: UniV3 TWAP, bit3: Chainlink spot, bit4: Chainlink ts
    // bit5: V3 stdev short, bit6: V3 stdev long

    // Immutable addresses of existing bridges (we call them; no changes required there)
    address public immutable uniV2Bridge;
    address public immutable uniV3Bridge;
    address public immutable uniV3TwapBridge;
    address public immutable chainlinkBridge;

    constructor(address _uniV2Bridge, address _uniV3Bridge, address _uniV3TwapBridge, address _chainlinkBridge) {
        uniV2Bridge = _uniV2Bridge;
        uniV3Bridge = _uniV3Bridge;
        uniV3TwapBridge = _uniV3TwapBridge;
        chainlinkBridge = _chainlinkBridge;
    }

    // Per-function independent caches (kept minimal; extend with TTL if needed)
    bytes public cacheV2;
    bytes public cacheV3;
    bytes public cacheTwap;
    bytes public cacheCl;
    bytes public cacheClTs;
    bytes public cacheStdvShort;
    bytes public cacheStdvLong;

    function getData(QuoteParams memory params) external override returns (bytes memory) {
        uint8 flags = params.functionFlags;
        uint8 mask = 0;
        uint128 v2Q; uint128 v3Q; uint128 twQ; uint128 clQ;
        uint64 ts; uint16 sdShort; uint16 sdLong;

        if ((flags & 0x01) != 0) { mask |= 0x01; v2Q = _getV2Q(params); }
        if ((flags & 0x02) != 0) { mask |= 0x02; v3Q = _getV3Q(params); }
        if ((flags & 0x04) != 0) { mask |= 0x04; twQ = _getV3TWAPQ(params); }
        // Chainlink optimization: fetch price and timestamp in single call if either is requested
        if ((flags & 0x18) != 0) { // 0x08 (price) OR 0x10 (timestamp)
            (uint128 clPrice, uint64 clTimestamp) = _getChainlinkPriceAndTs(params);
            if ((flags & 0x08) != 0) { mask |= 0x08; clQ = clPrice; }
            if ((flags & 0x10) != 0) { mask |= 0x10; ts = clTimestamp; }
        }
        if ((flags & 0x20) != 0) { mask |= 0x20; sdShort = _getStdvShortVal(params); }
        if ((flags & 0x40) != 0) { mask |= 0x40; sdLong = _getStdvLongVal(params); }

        return abi.encode(mask, v2Q, v3Q, twQ, clQ, ts, sdShort, sdLong);
    }

    // Convert 1e18 price to Q64.64
    function _qFrom1e18(uint256 x) internal pure returns (uint128) {
        return uint128((x << 64) / 1e18);
    }

    // The following functions call existing bridges and pack Q64.64 values consistently
    function _getV2(QuoteParams memory params) internal returns (bytes memory) {
        try IDataBridge(uniV2Bridge).getData(params) returns (bytes memory raw) {
            (uint256 spot, ) = abi.decode(raw, (uint256, uint256));
            if (spot == 0) return abi.encode(uint128(0));
            uint128 q = _qFrom1e18(spot);
            return abi.encode(q);
        } catch {
            return abi.encode(uint128(0));
        }
    }

    function _getV2Q(QuoteParams memory params) internal returns (uint128) {
        try IDataBridge(uniV2Bridge).getData(params) returns (bytes memory raw) {
            (uint256 spot, ) = abi.decode(raw, (uint256, uint256));
            return spot == 0 ? uint128(0) : _qFrom1e18(spot);
        } catch { return 0; }
    }

    function _getV3(QuoteParams memory params) internal returns (bytes memory) {
        try IDataBridge(uniV3Bridge).getData(params) returns (bytes memory raw) {
            (uint256 spot, ) = abi.decode(raw, (uint256, uint256));
            if (spot == 0) return abi.encode(uint128(0));
            uint128 q = _qFrom1e18(spot);
            return abi.encode(q);
        } catch {
            return abi.encode(uint128(0));
        }
    }

    function _getV3Q(QuoteParams memory params) internal returns (uint128) {
        try IDataBridge(uniV3Bridge).getData(params) returns (bytes memory raw) {
            (uint256 spot, ) = abi.decode(raw, (uint256, uint256));
            return spot == 0 ? uint128(0) : _qFrom1e18(spot);
        } catch { return 0; }
    }

    function _getV3TWAP(QuoteParams memory params) internal returns (bytes memory) {
        try IDataBridge(uniV3TwapBridge).getData(params) returns (bytes memory raw) {
            (uint256 twap, ) = abi.decode(raw, (uint256, uint256));
            if (twap == 0) return abi.encode(uint128(0));
            uint128 q = _qFrom1e18(twap);
            return abi.encode(q);
        } catch {
            return abi.encode(uint128(0));
        }
    }

    function _getV3TWAPQ(QuoteParams memory params) internal returns (uint128) {
        try IDataBridge(uniV3TwapBridge).getData(params) returns (bytes memory raw) {
            (uint256 twap, ) = abi.decode(raw, (uint256, uint256));
            return twap == 0 ? uint128(0) : _qFrom1e18(twap);
        } catch { return 0; }
    }

    function _getChainlink(QuoteParams memory params) internal returns (bytes memory) {
        try IDataBridge(chainlinkBridge).getData(params) returns (bytes memory raw) {
            (uint256 spot, ) = abi.decode(raw, (uint256, uint256));
            if (spot == 0) return abi.encode(uint128(0));
            uint128 q = _qFrom1e18(spot);
            return abi.encode(q);
        } catch {
            return abi.encode(uint128(0));
        }
    }

    // Optimized Chainlink function: fetch price and timestamp in single call
    function _getChainlinkPriceAndTs(QuoteParams memory params) internal returns (uint128 price, uint64 timestamp) {
        try IDataBridge(chainlinkBridge).getData(params) returns (bytes memory raw) {
            (uint256 spot, uint256 ts) = abi.decode(raw, (uint256, uint256));
            price = spot == 0 ? uint128(0) : _qFrom1e18(spot);
            timestamp = uint64(ts);
        } catch { 
            price = 0; 
            timestamp = 0; 
        }
    }

    // Legacy functions for backward compatibility (now call optimized version)
    function _getChainlinkQ(QuoteParams memory params) internal returns (uint128) {
        (uint128 price, ) = _getChainlinkPriceAndTs(params);
        return price;
    }

    function _getChainlinkTsVal(QuoteParams memory params) internal returns (uint64) {
        (, uint64 timestamp) = _getChainlinkPriceAndTs(params);
        return timestamp;
    }

    function _getStdvShort(QuoteParams memory) internal pure returns (bytes memory) { return abi.encode(uint16(0)); }
    function _getStdvLong(QuoteParams memory) internal pure returns (bytes memory) { return abi.encode(uint16(0)); }
    function _getStdvShortVal(QuoteParams memory) internal pure returns (uint16) { return 0; }
    function _getStdvLongVal(QuoteParams memory) internal pure returns (uint16) { return 0; }
}

