contracts/Core/libraries/AssetTransferLi.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

library AssetTransferLib {
    error AssetTransferLib__IncorrectEthSent();
    error AssetTransferLib__EthNotExpected();
    error AssetTransferLib__Erc20TransferFailed();
    /// @notice Handles transfer of ERC20 or ETH from user to contract
    /// @param asset The asset address (address(0) for ETH)
    /// @param from The sender address
    /// @param amount The amount to transfer
    /// @param msgValue The msg.value passed to the function (for ETH validation)
    function transferIn(address asset, address from, uint256 amount, uint256 msgValue) internal {
        if (asset == address(0)) {
            // ETH
            if (msgValue != amount) revert AssetTransferLib__IncorrectEthSent();
        } else {
            // ERC20
            if (msgValue != 0) revert AssetTransferLib__EthNotExpected();
            if (!IERC20(asset).transferFrom(from, address(this), amount)) revert AssetTransferLib__Erc20TransferFailed();
        }
    }

    /// @notice Handles transfer of ERC20 or ETH from contract to recipient
    /// @param asset The asset address (address(0) for ETH)
    /// @param recipient The recipient address
    /// @param amount The amount to transfer
    function transferOut(address asset, address recipient, uint256 amount) internal {
        if (asset == address(0)) {
            // ETH
            payable(recipient).transfer(amount);
        } else {
            // ERC20
            if (!IERC20(asset).transfer(recipient, amount)) revert AssetTransferLib__Erc20TransferFailed();
        }
    }
}

contracts/Core/libraries/FlashAccounting.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @title FlashAccounting
/// @notice Minimal transient-storage based per-session ledger for user/token deltas
library FlashAccounting {
    bytes32 private constant FLASH_DOMAIN = keccak256("dex.flash.accounting:v1");
    bytes32 private constant FLASH_SESSION = keccak256("dex.flash.session:v1");
    bytes32 private constant FLASH_ACTIVE_USER = keccak256("dex.flash.active.user:v1");

    function _slot(address callerContract, address user, address token) private pure returns (bytes32 slot) {
        // Unique slot per (contract, user, token)
        slot = keccak256(abi.encodePacked(FLASH_DOMAIN, callerContract, user, token));
    }

    /// @notice Add to the user's net delta for a given token
    /// @dev Negative delta means the user owes token; positive means the user is owed token
    function addDelta(address user, address token, int256 delta) internal {
        bytes32 slot = _slot(address(this), user, token);
        assembly {
            let old := tload(slot)
            let new := add(old, delta)
            tstore(slot, new)
        }
    }

    /// @notice Get the user's current net delta for a given token
    function getDelta(address user, address token) internal view returns (int256 current) {
        bytes32 slot = _slot(address(this), user, token);
        assembly {
            current := tload(slot)
        }
    }

    /// @notice Clear the user's delta for a given token
    function clearDelta(address user, address token) internal {
        bytes32 slot = _slot(address(this), user, token);
        assembly {
            tstore(slot, 0)
        }
    }

    function _sessionSlot(address callerContract, address user) private pure returns (bytes32 slot) {
        slot = keccak256(abi.encodePacked(FLASH_SESSION, callerContract, user));
    }

    function startSession(address user) internal {
        bytes32 slot = _sessionSlot(address(this), user);
        assembly {
            tstore(slot, 1)
        }
    }

    function endSession(address user) internal {
        bytes32 slot = _sessionSlot(address(this), user);
        assembly {
            tstore(slot, 0)
        }
    }

    function isSessionActive(address user) internal view returns (bool active) {
        bytes32 slot = _sessionSlot(address(this), user);
        assembly {
            active := tload(slot)
        }
    }

    function setActiveUser(address user) internal {
        bytes32 slot = keccak256(abi.encodePacked(FLASH_ACTIVE_USER, address(this)));
        assembly {
            tstore(slot, user)
        }
    }

    function clearActiveUser() internal {
        bytes32 slot = keccak256(abi.encodePacked(FLASH_ACTIVE_USER, address(this)));
        assembly {
            tstore(slot, 0)
        }
    }

    function getActiveUser() internal view returns (address user) {
        bytes32 slot = keccak256(abi.encodePacked(FLASH_ACTIVE_USER, address(this)));
        assembly {
            user := tload(slot)
        }
    }
}

contracts/Core/libraries/MarkingHelper.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;
import {Markings} from "../structs/Markings.sol";

library MarkingHelper {
    // Bit layout in bytes3 (low to high):
    // [0]=data0 [1]=data1 [2]=data2 [3]=data3 [4..15]=bucketID (12 bits) [16..19]=reserved [20..23]=extraBridgeSlot (4 bits)
    function decodeMarkings(bytes3 markings) internal pure returns (Markings memory result) {
        uint24 data = uint24(markings);
        result.data0 = (data & 0x1) != 0;
        result.data1 = ((data >> 1) & 0x1) != 0;
        result.data2 = ((data >> 2) & 0x1) != 0;
        result.data3 = ((data >> 3) & 0x1) != 0;
        result.bucketID = uint16((data >> 4) & 0xFFF);
        uint8 extra = uint8((data >> 20) & 0xF);
        result.extraBridgeSlot = extra; // 0 means none; 4..15 indicate overall slot index
        return result;
    }
}

contracts/Core/libraries/PoolIDAssembly..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

library PoolIDAssembly {
    /// @notice Creates a pool ID as a uint256 for use with ERC6909.
    /// @dev Must match off-chain calculation used in tests: keccak256(abi.encodePacked(asset0, asset1, quoter, markings)).
    function assemblePoolID(
        address asset0,
        address asset1,
        address quoter,
        bytes3 markings
    ) internal pure returns (uint256 poolID) {
        // Canonicalize asset order: asset0 < asset1
        (address a0, address a1) = asset0 < asset1 ? (asset0, asset1) : (asset1, asset0);
        poolID = uint256(keccak256(abi.encodePacked(a0, a1, quoter, markings)));
    }
}

contracts/Core/libraries/PoolManagerLib..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

// Custom errors for gas-efficient reverts
error PoolManager__InsufficientLiquidityMinted();
error PoolManager__InsufficientAsset0(uint256 required, uint256 available);
error PoolManager__InsufficientAsset1(uint256 required, uint256 available);
error PoolManager__PoolAlreadyExists(uint256 poolID);
error PoolManager__NoHops();
error PoolManager__NoMarkings();
error PoolManager__MismatchedArrays();

import {PoolIDAssembly} from "./PoolIDAssembly.sol";
import {AssetTransferLib} from "./AssetTransferLib.sol";
import {FlashAccounting} from "./FlashAccounting.sol";
import {SwapParams} from "../structs/SwapParams.sol";
import {IQuoteRouter} from "../interfaces/internal/IQuoteRouter.sol";
import {Hop} from "../structs/Hop.sol";
import {PoolInfo} from "../structs/PoolInfo.sol";
import {CommitRevealLib} from "../MEV/CommitRevealLib.sol";
import {AtomicExecutionLib} from "../MEV/AtomicExecutionLib.sol";
import {AccessControlLib} from "../MEV/AccessControlLib.sol";
import {SimpleGovernanceLib} from "./SimpleGovernanceLib.sol";

// (interface moved to contracts/interfaces/internal/IQuoteRouter.sol)

/// @notice Library containing core calculation logic for PoolManager
/// @dev Pure calculation functions and simple storage management
library PoolManagerLib {

    /*//////////////////////////////////////////////////////////////
                                 STRUCTS
    //////////////////////////////////////////////////////////////*/

    // PoolInfo and Hop moved to structs folder

    /*//////////////////////////////////////////////////////////////
                                 STORAGE
    //////////////////////////////////////////////////////////////*/

    struct PoolManagerStorage {
        // Total liquidity (shares) issued per pool
        mapping(uint256 => uint256) totalLiquidity;
        // Pool asset balances (poolID -> packed uint256) - BOTH ASSETS IN SINGLE SLOT!
        // Lower 128 bits = asset0, Upper 128 bits = asset1
        mapping(uint256 => uint256) poolInventories;
        // Pool information for external queries (poolID -> PoolInfo)
        mapping(uint256 => PoolInfo) poolInfos;
        // Protocol fee configuration (applies on liquidity events only)
        address protocolTreasury;          // address(0) disables fee
        uint16 protocolFeeBps;             // e.g., 700 for 7% of profit
        // Profit baselines per pool - dual asset tracking to prevent rate change artifacts
        mapping(uint256 => uint256) profitBaselineAsset0;
        mapping(uint256 => uint256) profitBaselineAsset1;
        // Commit-reveal MEV protection data
        CommitRevealLib.CommitData commitData;
        // Atomic execution MEV protection data
        AtomicExecutionLib.AtomicExecutionData atomicData;
        // Access control MEV protection data
        AccessControlLib.UniversalAccessConfig accessData;
        // Simple governance data (ultra-efficient single SLOAD)
        SimpleGovernanceLib.SimpleGovernanceData simpleGovernance;
    }

    /*//////////////////////////////////////////////////////////////
                                 EVENTS
    //////////////////////////////////////////////////////////////*/

    event PoolCreated(uint256 indexed poolID, address asset0, address asset1, address quoter, bytes3 markings);
    event ProtocolFeeConfigured(address treasury, uint16 feeBps);
    event ProtocolFeeCharged(uint256 indexed poolID, address treasury, uint256 feeAsset0, uint256 feeAsset1);

    /*//////////////////////////////////////////////////////////////
                            POOL CREATION
    //////////////////////////////////////////////////////////////*/

    /// @notice Creates a new pool with canonical asset ordering (asset0 < asset1)
    /// @param self Storage reference
    /// @param asset0 First asset address
    /// @param asset1 Second asset address  
    /// @param quoter Quoter contract address
    /// @param markings Pool configuration markings
    /// @return poolID The unique identifier for the created pool
    function createPool(
        PoolManagerStorage storage self,
        address asset0,
        address asset1,
        address quoter,
        bytes3 markings
    ) internal returns (uint256 poolID) {
        // PoolIDAssembly will canonicalize the order internally
        poolID = PoolIDAssembly.assemblePoolID(asset0, asset1, quoter, markings);

        // Check if pool already exists by checking if quoter is non-zero
        if (self.poolInfos[poolID].quoter != address(0)) {
            revert PoolManager__PoolAlreadyExists(poolID);
        }

        // Store pool information with canonical asset order
        (address sortedAsset0, address sortedAsset1) = asset0 < asset1 ? (asset0, asset1) : (asset1, asset0);
        self.poolInfos[poolID] = PoolInfo({
            asset0: sortedAsset0,
            asset1: sortedAsset1,
            quoter: quoter,
            markings: markings
        });

        // Emit with canonical order for consistency
        emit PoolCreated(poolID, sortedAsset0, sortedAsset1, quoter, markings);
    }

    /// @notice Get pool information by poolID
    /// @param self Storage reference
    /// @param poolID Pool identifier
    /// @return poolInfo Pool information structure
    function getPoolInfo(
        PoolManagerStorage storage self,
        uint256 poolID
    ) internal view returns (PoolInfo memory poolInfo) {
        return self.poolInfos[poolID];
    }

    /*//////////////////////////////////////////////////////////////
                            LIQUIDITY CALCULATIONS
    //////////////////////////////////////////////////////////////*/

    /// @notice Calculates liquidity to mint for new providers
    /// @param self Storage reference
    /// @param amount0 Amount of asset0 being added
    /// @param amount1 Amount of asset1 being added
    /// @param poolAsset0 Current pool balance of asset0
    /// @param poolAsset1 Current pool balance of asset1
    /// @param poolID Pool identifier for totalLiquidity access
    /// @param rate Exchange rate (asset0/asset1) 1e18 fixed point
    /// @return liquidity Amount of liquidity shares to mint
    function calculateLiquidityToMint(
        PoolManagerStorage storage self,
        uint256 amount0,
        uint256 amount1,
        uint128 poolAsset0,
        uint128 poolAsset1,
        uint256 poolID,
        uint256 rate
    ) internal view returns (uint256 liquidity) {
        // For first liquidity provision, use simple geometric mean
        if (self.totalLiquidity[poolID] == 0) {
            // Simple approach: use the sum of both amounts as initial liquidity
            liquidity = amount0 + amount1;
        } else {
            // Convert amount1 to amount0 equivalent using rate: amount1_in_a0 = amount1 * rate / 1e18
            uint256 valueAdded = amount0 + (amount1 * rate) / 1e18;
            uint256 poolValue = uint256(poolAsset0) + (uint256(poolAsset1) * rate) / 1e18;

            // Proportional to pool
            liquidity = mulDiv(valueAdded, self.totalLiquidity[poolID], poolValue);
        }

        if (liquidity == 0) revert PoolManager__InsufficientLiquidityMinted();
    }

    /// @notice 512-bit mulDiv from Uniswap FullMath (simplified)
    function mulDiv(uint256 a, uint256 b, uint256 denominator) internal pure returns (uint256 result) {
        unchecked {
            uint256 prod0;
            uint256 prod1;
            assembly {
                let mm := mulmod(a, b, not(0))
                prod0 := mul(a, b)
                prod1 := sub(sub(mm, prod0), lt(mm, prod0))
            }
            if (prod1 == 0) {
                require(denominator > 0);
                assembly { result := div(prod0, denominator) }
                return result;
            }
            require(denominator > prod1);
            uint256 remainder;
            assembly {
                remainder := mulmod(a, b, denominator)
                prod1 := sub(prod1, gt(remainder, prod0))
                prod0 := sub(prod0, remainder)
            }
            uint256 twos = denominator & (~denominator + 1);
            assembly {
                denominator := div(denominator, twos)
                prod0 := div(prod0, twos)
                twos := add(div(sub(0, twos), twos), 1)
            }
            prod0 |= prod1 * twos;
            uint256 inv = 3 * denominator ^ 2;
            inv *= 2 - denominator * inv;
            inv *= 2 - denominator * inv;
            inv *= 2 - denominator * inv;
            inv *= 2 - denominator * inv;
            inv *= 2 - denominator * inv;
            inv *= 2 - denominator * inv;
            result = prod0 * inv;
        }
    }

    /*//////////////////////////////////////////////////////////////
                            STORAGE ACCESS FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /// @notice Get current pool inventory - SINGLE STORAGE READ!
    /// @param self Storage reference
    /// @param poolId Pool identifier
    /// @return asset0 Asset0 balance
    /// @return asset1 Asset1 balance
    function getInventory(
        PoolManagerStorage storage self,
        uint256 poolId
    ) internal view returns (uint128 asset0, uint128 asset1) {
        uint256 packed = self.poolInventories[poolId]; // Single SLOAD!
        asset0 = uint128(packed);              // Lower 128 bits
        asset1 = uint128(packed >> 128);       // Upper 128 bits
    }

    /// @notice Return the pool value in asset0 terms using provided rate (asset0 per 1e18 of asset1)
    function getPoolValueInAsset0(
        uint128 poolAsset0,
        uint128 poolAsset1,
        uint256 rateAsset0Per1e18Asset1
    ) internal pure returns (uint256 valueAsset0) {
        // value = asset0 + asset1_in_asset0
        uint256 asset1AsAsset0 = (uint256(poolAsset1) * rateAsset0Per1e18Asset1) / 1e18;
        valueAsset0 = uint256(poolAsset0) + asset1AsAsset0;
    }

    /// @notice Derive inventory-implied rate (asset0 per 1e18 of asset1)
    function getInventoryRateAsset0Per1e18Asset1(
        uint128 poolAsset0,
        uint128 poolAsset1
    ) internal pure returns (uint256 rate) {
        if (poolAsset1 == 0) return 0;
        rate = (uint256(poolAsset0) * 1e18) / uint256(poolAsset1);
    }

    /// @notice Update pool inventory - SINGLE STORAGE OPERATION!
    /// @param self Storage reference
    /// @param poolId Pool identifier
    /// @param asset0Delta Change for asset0 (positive = add, negative = subtract)
    /// @param asset1Delta Change for asset1 (positive = add, negative = subtract)
    function updateInventory(
        PoolManagerStorage storage self,
        uint256 poolId,
        int128 asset0Delta,
        int128 asset1Delta
    ) internal {
        uint256 packed = self.poolInventories[poolId]; // Single SLOAD
        uint128 asset0 = uint128(packed);         // Lower 128 bits
        uint128 asset1 = uint128(packed >> 128);  // Upper 128 bits

        // Apply delta changes
        asset0 = uint128(int128(asset0) + asset0Delta);
        asset1 = uint128(int128(asset1) + asset1Delta);

        // Pack and store only if changed - saves gas on no-op updates
        uint256 newPacked = uint256(asset0) | (uint256(asset1) << 128);
        if (newPacked != packed) {
            self.poolInventories[poolId] = newPacked; // Single SSTORE when needed
        }
    }

    /// @notice Configure protocol fee. Setting treasury to 0 disables fees.
    function configureProtocolFee(
        PoolManagerStorage storage self,
        address treasury,
        uint16 feeBps
    ) internal {
        self.protocolTreasury = treasury;
        self.protocolFeeBps = feeBps;
        emit ProtocolFeeConfigured(treasury, feeBps);
    }

    /// @notice Charge protocol fee on profit since last checkpoint using dual-token method.
    /// @dev Only charges fee when BOTH assets increased (prevents rate change artifacts).
    ///      Uses minimum profit approach for maximum conservatism.
    function chargeProtocolProfitFee(
        PoolManagerStorage storage self,
        uint256 poolID,
        uint256 rateAsset0Per1e18Asset1
    ) internal {
        address treasury = self.protocolTreasury;
        uint16 feeBps = self.protocolFeeBps;
        if (treasury == address(0) || feeBps == 0) {
            return;
        }

        // Get current balances and baselines
        (uint128 currentA0, uint128 currentA1) = getInventory(self, poolID);
        uint128 baselineA0 = uint128(self.profitBaselineAsset0[poolID]);
        uint128 baselineA1 = uint128(self.profitBaselineAsset1[poolID]);

        if (currentA0 == 0 && currentA1 == 0) {
            // Nothing to charge; update baselines to zero
            self.profitBaselineAsset0[poolID] = 0;
            self.profitBaselineAsset1[poolID] = 0;
            return;
        }

        // Calculate raw changes in both assets
        int256 deltaA0 = int256(uint256(currentA0)) - int256(uint256(baselineA0));
        int256 deltaA1 = int256(uint256(currentA1)) - int256(uint256(baselineA1));

        // Only charge fee if BOTH assets increased (genuine trading profit)
        if (deltaA0 <= 0 || deltaA1 <= 0) {
            // No profit or loss in at least one asset - no fee
            return;
        }

        // Convert deltaA1 to asset0 terms for comparison
        uint256 delta1InA0 = (uint256(deltaA1) * rateAsset0Per1e18Asset1) / 1e18;

        // Take minimum profit (most conservative approach)
        uint256 profitA0 = uint256(deltaA0) < delta1InA0 ? uint256(deltaA0) : delta1InA0;

        uint256 feeValueA0 = (profitA0 * uint256(feeBps)) / 10000;
        if (feeValueA0 == 0) {
            return;
        }

        // Split fee proportionally to current composition to preserve pool ratio
        uint256 asset1AsA0 = (uint256(currentA1) * rateAsset0Per1e18Asset1) / 1e18;
        uint256 totalA0Value = uint256(currentA0) + asset1AsA0;
        if (totalA0Value == 0) {
            return;
        }

        uint256 feeA0Part = (feeValueA0 * uint256(currentA0)) / totalA0Value;
        uint256 feeA1PartInA0 = feeValueA0 - feeA0Part;
        // Convert asset0-value to asset1 amount: a1 = value_in_a0 * 1e18 / rate
        uint256 feeA1Part = (feeA1PartInA0 * 1e18) / (rateAsset0Per1e18Asset1 == 0 ? 1 : rateAsset0Per1e18Asset1);

        // Bound by current balances
        if (feeA0Part > currentA0) feeA0Part = currentA0;
        if (feeA1Part > currentA1) feeA1Part = currentA1;

        // Update inventory
        updateInventory(self, poolID, -int128(uint128(feeA0Part)), -int128(uint128(feeA1Part)));

        // Transfer out to treasury
        PoolInfo memory info = self.poolInfos[poolID];
        if (feeA0Part > 0) {
            AssetTransferLib.transferOut(info.asset0, treasury, feeA0Part);
        }
        if (feeA1Part > 0) {
            AssetTransferLib.transferOut(info.asset1, treasury, feeA1Part);
        }

        emit ProtocolFeeCharged(poolID, treasury, feeA0Part, feeA1Part);
    }

    /// @notice Update profit baselines for both assets to current inventory levels.
    /// @dev Called after liquidity events to reset profit measurement baseline.
    function updateProfitBaseline(
        PoolManagerStorage storage self,
        uint256 poolID,
        uint128 newBaselineAsset0,
        uint128 newBaselineAsset1
    ) internal {
        self.profitBaselineAsset0[poolID] = uint256(newBaselineAsset0);
        self.profitBaselineAsset1[poolID] = uint256(newBaselineAsset1);
    }

    /// @notice Get current profit baselines for both assets.
    function getProfitBaselines(
        PoolManagerStorage storage self,
        uint256 poolID
    ) internal view returns (uint128 baselineAsset0, uint128 baselineAsset1) {
        baselineAsset0 = uint128(self.profitBaselineAsset0[poolID]);
        baselineAsset1 = uint128(self.profitBaselineAsset1[poolID]);
    }

    /*//////////////////////////////////////////////////////////////
                            SWAP VALIDATION
    //////////////////////////////////////////////////////////////*/

    /// @notice Validates if a swap can be executed with given inventory
    /// @param poolAsset0 Current asset0 balance
    /// @param poolAsset1 Current asset1 balance
    /// @param amountOut Expected output amount
    /// @param zeroForOne Direction of swap (true = asset0 -> asset1, false = asset1 -> asset0)
    function validateSwapInventory(
        uint128 poolAsset0,
        uint128 poolAsset1,
        uint256 amountOut,
        bool zeroForOne
    ) internal pure {
        // Check if pool has enough of the output asset
        if (zeroForOne) {
            // Swapping asset0 for asset1 - need enough asset1
            if (poolAsset1 < amountOut) revert PoolManager__InsufficientAsset1(amountOut, poolAsset1);
        } else {
            // Swapping asset1 for asset0 - need enough asset0
            if (poolAsset0 < amountOut) revert PoolManager__InsufficientAsset0(amountOut, poolAsset0);
        }
    }

    /*//////////////////////////////////////////////////////////////
                            ASSET TRANSFERS
    //////////////////////////////////////////////////////////////*/

    /// @notice Handles asset transfers for liquidity operations
    /// @param asset0 Asset0 address
    /// @param asset1 Asset1 address
    /// @param amount0 Amount of asset0 to transfer
    /// @param amount1 Amount of asset1 to transfer
    /// @param msgValue ETH value sent with transaction
    /// @param transferIn True for transferIn, false for transferOut
    /// @param recipient Recipient address (for transferOut)
    function handleAssetTransfers(
        address asset0,
        address asset1,
        uint256 amount0,
        uint256 amount1,
        uint256 msgValue,
        bool transferIn,
        address recipient
    ) internal {
        if (transferIn) {
            // Transfer assets in
            if (amount0 > 0) {
                AssetTransferLib.transferIn(asset0, recipient, amount0, msgValue);
            }
            if (amount1 > 0) {
                AssetTransferLib.transferIn(asset1, recipient, amount1, 0); // No ETH for asset1
            }
        } else {
            // Transfer assets out
            if (amount0 > 0) {
                AssetTransferLib.transferOut(asset0, recipient, amount0);
            }
            if (amount1 > 0) {
                AssetTransferLib.transferOut(asset1, recipient, amount1);
            }
        }
    }

    /*//////////////////////////////////////////////////////////////
                            BATCH SWAP PROCESSING
    //////////////////////////////////////////////////////////////*/

    /// @notice Execute batch swap with automatic quoter selection
    /// @param self Storage reference
    /// @param hops Array of hops to execute
    /// @param amountIn Initial input amount
    /// @param msgValue ETH value for first hop
    /// @param recipient Recipient address
    /// @param quoterRouter QuoterRouter contract for quote calls
    /// @return amountOut Final output amount
    function executeBatchSwap(
        PoolManagerStorage storage self,
        Hop[] calldata hops,
        uint256 amountIn,
        uint256 msgValue,
        address recipient,
        address quoterRouter
    ) internal returns (uint256 amountOut) {
        uint256 numHops = hops.length;
        if (numHops == 0) revert PoolManager__NoHops();

        uint256 intermediateAmount = amountIn;
        // Determine user-facing input and output assets once
        address inputAsset = hops[0].zeroForOne ? hops[0].asset0 : hops[0].asset1;
        address outputAsset = hops[numHops - 1].zeroForOne ? hops[numHops - 1].asset1 : hops[numHops - 1].asset0;

        for (uint256 i = 0; i < numHops; ) {
            Hop calldata hop = hops[i];
            bool isFirstHop = i == 0;
            bool isLastHop = i == numHops - 1;

            intermediateAmount = executeHop(
                self,
                hop,
                intermediateAmount,
                isFirstHop ? msgValue : 0,
                isFirstHop,
                isLastHop,
                recipient,
                quoterRouter
            );
            unchecked { ++i; }
        }

        // Perform settlement only if a session is not active; otherwise defer to final session settle
        if (!FlashAccounting.isSessionActive(recipient)) {
            _settleUserDeltas(recipient, inputAsset, outputAsset, amountIn, intermediateAmount, msgValue);
        }

        return intermediateAmount;
    }

    /// @notice Execute a single hop (individual or batch based on array length)
    /// @param self Storage reference
    /// @param hop Hop to execute
    /// @param inputAmount Input amount for this hop
    /// @param isFirstHop Whether this is the first hop
    /// @param isLastHop Whether this is the last hop
    /// @param recipient Recipient address
    /// @param quoterRouter QuoterRouter contract for quote calls
    /// @return outputAmount Output amount from this hop
    function executeHop(
        PoolManagerStorage storage self,
        Hop calldata hop,
        uint256 inputAmount,
        uint256 /*msgValue*/,
        bool isFirstHop,
        bool isLastHop,
        address recipient,
        address quoterRouter
    ) internal returns (uint256 outputAmount) {
        if (hop.markings.length == 0) revert PoolManager__NoMarkings();
        if (hop.amounts.length != hop.markings.length) revert PoolManager__MismatchedArrays();

        if (hop.markings.length == 1) {
            // Single hop - use individual quote
            return executeSingleHop(
                self,
                hop.asset0,
                hop.asset1,
                hop.quoter,
                hop.markings[0],
                hop.zeroForOne,
                inputAmount,
                0,
                isFirstHop,
                isLastHop,
                recipient,
                quoterRouter
            );
        } else {
            // Multiple hops with same quoter - use batch quote
            return executeBatchHopsWithQuoter(
                self,
                hop,
                inputAmount,
                isFirstHop,
                isLastHop,
                recipient,
                quoterRouter
            );
        }
    }

    /// @notice Execute multiple hops with the same quoter using batch quote
    /// @param self Storage reference
    /// @param hop Batch hop with multiple markings/amounts
    /// @param inputAmount Input amount for this batch
    /// @param isFirstHop Whether this is the first hop
    /// @param isLastHop Whether this is the last hop
    /// @param recipient Recipient address
    /// @param quoterRouter QuoterRouter contract for quote calls
    /// @return outputAmount Output amount from this batch
    function executeBatchHopsWithQuoter(
        PoolManagerStorage storage self,
        Hop calldata hop,
        uint256 inputAmount,
        bool isFirstHop,
        bool isLastHop,
        address recipient,
        address quoterRouter
    ) internal returns (uint256 outputAmount) {
        uint256 batchSize = hop.markings.length;

        // Flash accounting: record user owes input for first hop, defer transfer to settlement
        if (isFirstHop) {
            address inAsset = hop.zeroForOne ? hop.asset0 : hop.asset1;
            FlashAccounting.addDelta(recipient, inAsset, -int256(inputAmount));
        }

        // Prepare batch parameters for quoter
        uint128[] memory asset0Balances = new uint128[](batchSize);
        uint128[] memory asset1Balances = new uint128[](batchSize);

        // Get pool balances for all hops in the batch
        for (uint256 i = 0; i < batchSize; ) {
            uint256 poolID = PoolIDAssembly.assemblePoolID(hop.asset0, hop.asset1, hop.quoter, hop.markings[i]);
            (asset0Balances[i], asset1Balances[i]) = getInventory(self, poolID);
            unchecked { ++i; }
        }

        // Create SwapParams for batch quote
        SwapParams memory swapParams = SwapParams({
            asset0: hop.asset0,
            asset1: hop.asset1,
            quoter: hop.quoter,
            amount: hop.amounts,
            zeroForOne: hop.zeroForOne,
            marking: hop.markings,
            traderProtection: 0x00000000
        });

        // Get batch quotes using QuoteRouter
        (uint256[] memory quotes, uint256[] memory poolIDs) = IQuoteRouter(quoterRouter).getQuoteBatch(swapParams, asset0Balances, asset1Balances);

        // Execute all hops in the batch
        for (uint256 i = 0; i < batchSize; ) {
            uint256 hopAmountIn = hop.amounts[i];
            uint256 hopAmountOut = quotes[i];

            // Validate swap inventory
            validateSwapInventory(
                asset0Balances[i], 
                asset1Balances[i], 
                hopAmountOut, 
                hop.zeroForOne
            );

            // Update inventory
            updateInventory(
                self,
                poolIDs[i],
                hop.zeroForOne ? int128(uint128(hopAmountIn)) : -int128(uint128(hopAmountOut)),
                hop.zeroForOne ? -int128(uint128(hopAmountOut)) : int128(uint128(hopAmountIn))
            );
            unchecked { ++i; }
        }

        // Flash accounting: record user is owed output for last hop, defer transfer to settlement
        if (isLastHop) {
            address outAsset = hop.zeroForOne ? hop.asset1 : hop.asset0;
            FlashAccounting.addDelta(recipient, outAsset, int256(quotes[batchSize - 1]));
        }

        return quotes[batchSize - 1];
    }

    /// @notice Execute a single hop
    /// @param self Storage reference
    /// @param asset0 First asset address
    /// @param asset1 Second asset address
    /// @param quoter Quoter contract address
    /// @param marking Pool marking
    /// @param zeroForOne Swap direction
    /// @param inputAmount Input amount
    /// @param isFirstHop Whether this is the first hop
    /// @param isLastHop Whether this is the last hop
    /// @param recipient Recipient address
    /// @param quoterRouter QuoterRouter contract for quote calls
    /// @return outputAmount Output amount
    function executeSingleHop(
        PoolManagerStorage storage self,
        address asset0,
        address asset1,
        address quoter,
        bytes3 marking,
        bool zeroForOne,
        uint256 inputAmount,
        uint256 /*msgValue*/,
        bool isFirstHop,
        bool isLastHop,
        address recipient,
        address quoterRouter
    ) internal returns (uint256 outputAmount) {
        uint256 poolID = PoolIDAssembly.assemblePoolID(asset0, asset1, quoter, marking);

        // Flash accounting: record user owes input for first hop, defer transfer to settlement
        if (isFirstHop) {
            address inAsset = zeroForOne ? asset0 : asset1;
            FlashAccounting.addDelta(recipient, inAsset, -int256(inputAmount));
        }

        (uint128 poolAsset0, uint128 poolAsset1) = getInventory(self, poolID);

        outputAmount = getHopQuote(
            self,
            asset0,
            asset1,
            quoter,
            marking,
            zeroForOne,
            inputAmount,
            poolAsset0,
            poolAsset1,
            quoterRouter
        );

        validateSwapInventory(poolAsset0, poolAsset1, outputAmount, zeroForOne);

        updateInventory(
            self,
            poolID,
            zeroForOne ? int128(uint128(inputAmount)) : -int128(uint128(outputAmount)),
            zeroForOne ? -int128(uint128(outputAmount)) : int128(uint128(inputAmount))
        );

        if (isLastHop) {
            // Flash accounting: record user is owed output for last hop, defer transfer to settlement
            address outAsset = zeroForOne ? asset1 : asset0;
            FlashAccounting.addDelta(recipient, outAsset, int256(outputAmount));
        }
        // For intermediate hops, the output asset stays in the contract for the next hop
    }

    /// @notice Get quote for a single hop
    /// @param asset0 First asset address
    /// @param asset1 Second asset address
    /// @param quoter Quoter contract address
    /// @param marking Pool marking
    /// @param zeroForOne Swap direction
    /// @param amountIn Input amount
    /// @param poolAsset0 Pool asset0 balance
    /// @param poolAsset1 Pool asset1 balance
    /// @param quoterRouter QuoterRouter contract for quote calls
    /// @return amountOut Output amount
    function getHopQuote(
        PoolManagerStorage storage /*self*/,
        address asset0,
        address asset1,
        address quoter,
        bytes3 marking,
        bool zeroForOne,
        uint256 amountIn,
        uint128 poolAsset0,
        uint128 poolAsset1,
        address quoterRouter
    ) internal returns (uint256 amountOut) {
        uint256[] memory amounts = new uint256[](1);
        amounts[0] = amountIn;
        bytes3[] memory marks = new bytes3[](1);
        marks[0] = marking;
        SwapParams memory p = SwapParams({
            asset0: asset0,
            asset1: asset1,
            quoter: quoter,
            amount: amounts,
            zeroForOne: zeroForOne,
            marking: marks,
            traderProtection: 0x00000000
        });
        (uint256 quote, ) = IQuoteRouter(quoterRouter).getQuote(p, poolAsset0, poolAsset1);
        amountOut = quote > 0
            ? quote
            : (zeroForOne ? (amountIn * 1300000000000000000) / 1e18 : (amountIn * 1e18) / 1300000000000000000);
    }

    /*//////////////////////////////////////////////////////////////
                            FLASH SETTLEMENT
    //////////////////////////////////////////////////////////////*/

    /// @notice Settle user's net deltas for two tokens (input/output) with minimal transfers
    function _settleUserDeltas(
        address user,
        address inputAsset,
        address outputAsset,
        uint256 /*amountIn*/,
        uint256 /*amountOut*/,
        uint256 msgValue
    ) internal {
        // Read deltas
        int256 inDelta = FlashAccounting.getDelta(user, inputAsset);
        int256 outDelta = FlashAccounting.getDelta(user, outputAsset);

        // If both assets are the same token, merge deltas
        if (inputAsset == outputAsset) {
            int256 net = inDelta + outDelta; // could be positive (pay user) or negative (collect from user)
            if (net < 0) {
                // collect from user
                uint256 collect = uint256(-net);
                AssetTransferLib.transferIn(inputAsset, user, collect, inputAsset == address(0) ? msgValue : 0);
            } else if (net > 0) {
                // pay user
                uint256 pay = uint256(net);
                AssetTransferLib.transferOut(outputAsset, user, pay);
            }
            // clear
            FlashAccounting.clearDelta(user, inputAsset);
            return;
        }

        // Different tokens: settle separately
        if (inDelta < 0) {
            uint256 collectIn = uint256(-inDelta);
            AssetTransferLib.transferIn(inputAsset, user, collectIn, inputAsset == address(0) ? msgValue : 0);
        } else if (inDelta > 0) {
            // Should not happen in this flow; clear defensively by paying out
            AssetTransferLib.transferOut(inputAsset, user, uint256(inDelta));
        }

        if (outDelta > 0) {
            AssetTransferLib.transferOut(outputAsset, user, uint256(outDelta));
        } else if (outDelta < 0) {
            // Should not happen in this flow; collect from user
            AssetTransferLib.transferIn(outputAsset, user, uint256(-outDelta), outputAsset == address(0) ? msgValue : 0);
        }

        // clear
        FlashAccounting.clearDelta(user, inputAsset);
        FlashAccounting.clearDelta(user, outputAsset);
    }

    /// @notice Settle an arbitrary list of tokens for a user (session settle)
    /// @dev Positive delta => pay user; Negative delta => collect from user
    function settleUserTokens(
        address user,
        address[] memory tokens,
        uint256 msgValue
    ) internal {
        uint256 length = tokens.length;
        for (uint256 i = 0; i < length; ) {
            address token = tokens[i];
            int256 delta = FlashAccounting.getDelta(user, token);
            if (delta != 0) {
                if (delta > 0) {
                    AssetTransferLib.transferOut(token, user, uint256(delta));
                } else {
                    AssetTransferLib.transferIn(token, user, uint256(-delta), token == address(0) ? msgValue : 0);
                }
                FlashAccounting.clearDelta(user, token);
            }
            unchecked { ++i; }
        }
    }
}

contracts/Core/libraries/SimpleGovernanc.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @title SimpleGovernanceLib
/// @notice Ultra-efficient governance with flag check + single SLOAD design
/// @dev Following user's optimized architecture for minimal overhead
library SimpleGovernanceLib {

    /*//////////////////////////////////////////////////////////////
                                CONSTANTS
    //////////////////////////////////////////////////////////////*/

    // Protocol pool flag in markings (use a HIGH BIT to avoid collisions with data flags)
    // Layout note: low 4 bits are used for data0..data3 in MarkingHelper.
    // We choose bit 16 (0x010000) for protocol features to avoid triggering data fetch logic.
    bytes3 constant PROTOCOL_POOL_FLAG = 0x010000;

    // Emergency states (packed in single storage slot)
    uint8 constant NORMAL = 0;
    uint8 constant PAUSED = 1;
    uint8 constant TIMELOCKED = 2;

    /*//////////////////////////////////////////////////////////////
                                STRUCTS
    //////////////////////////////////////////////////////////////*/

    struct SimpleGovernanceData {
        // Single mapping for pool states (poolId => state)
        mapping(uint256 => uint8) poolStates;    // 0=normal, 1=paused, 2=timelocked
        // Packed global state + paused pool count to keep runtime to ONE SLOAD in normal path
        // Layout: [0..7]=globalEmergencyState, [8..31]=pausedPoolsCount (24 bits)
        uint256 globalPackedState;
        // Governance addresses
        address protocolMultisig;                // Can create protocol pools
        address emergencyMultisig;               // Can pause/timelock
    }

    /*//////////////////////////////////////////////////////////////
                            VALIDATION (Minimal)
    //////////////////////////////////////////////////////////////*/

    /// @notice Check if swap is allowed (ultra-efficient)
    /// @param self Governance data
    /// @param markings Pool markings
    /// @param poolId Pool identifier (already generated)
    /// @return allowed Whether swap is allowed
    function validateSwap(
        SimpleGovernanceData storage self,
        bytes3 markings,
        uint256 poolId
    ) internal view returns (bool allowed) {

        // ULTRA-FAST PATH: Check flag first (cheapest operation)
        if ((markings & PROTOCOL_POOL_FLAG) == 0) {
            return true; // Not a protocol pool - no checks needed
        }

        // SINGLE SLOAD: Read packed global state
        uint256 packed = self.globalPackedState;
        uint8 globalState = uint8(packed);
        if (globalState != NORMAL) {
            return false; // Globally restricted
        }
        // If no pools are paused/timelocked individually, skip mapping read entirely
        uint32 pausedCount = uint32((packed >> 8) & 0xFFFFFF);
        if (pausedCount == 0) {
            return true;
        }
        // Rare path: some pools are paused; check only this pool
        uint8 poolState = self.poolStates[poolId];
        return poolState == NORMAL;
    }

    /*//////////////////////////////////////////////////////////////
                            POOL MANAGEMENT
    //////////////////////////////////////////////////////////////*/

    /// @notice Register protocol pool (called during creation)
    /// @param self Governance data
    /// @param poolId Pool identifier
    function registerProtocolPool(
        SimpleGovernanceData storage self,
        uint256 poolId
    ) internal {
        self.poolStates[poolId] = NORMAL; // Initialize as normal
    }

    /*//////////////////////////////////////////////////////////////
                            EMERGENCY CONTROLS
    //////////////////////////////////////////////////////////////*/

    /// @notice Emergency pause all protocol pools
    /// @param self Governance data
    /// @param newState Emergency state (PAUSED or TIMELOCKED)
    function setGlobalEmergencyState(
        SimpleGovernanceData storage self,
        uint8 newState
    ) internal {
        uint256 packed = self.globalPackedState;
        // Clear low 8 bits and set
        packed = (packed & ~uint256(0xFF)) | uint256(newState);
        self.globalPackedState = packed;
    }

    /// @notice Pause specific protocol pool
    /// @param self Governance data
    /// @param poolId Pool to pause
    /// @param newState Emergency state
    function pauseSpecificPool(
        SimpleGovernanceData storage self,
        uint256 poolId,
        uint8 newState
    ) internal {
        uint8 prev = self.poolStates[poolId];
        if (prev == newState) {
            return;
        }
        // Update paused pool count if transitioning between NORMAL and non-NORMAL
        uint256 packed = self.globalPackedState;
        uint32 count = uint32((packed >> 8) & 0xFFFFFF);
        bool prevPaused = (prev != NORMAL);
        bool newPaused = (newState != NORMAL);
        if (prevPaused && !newPaused) {
            // Unpausing
            if (count > 0) { count -= 1; }
        } else if (!prevPaused && newPaused) {
            // Pausing
            count += 1;
        }
        // Write back pool state and packed count
        self.poolStates[poolId] = newState;
        // Clear count bits and set
        packed = (packed & ~(uint256(0xFFFFFF) << 8)) | (uint256(count) << 8);
        self.globalPackedState = packed;
    }

    /*//////////////////////////////////////////////////////////////
                            ACCESS CONTROL
    //////////////////////////////////////////////////////////////*/

    /// @notice Check if caller can create protocol pools
    /// @param self Governance data
    /// @param creator Pool creator address
    /// @return authorized Whether creator is authorized
    function canCreateProtocolPool(
        SimpleGovernanceData storage self,
        address creator
    ) internal view returns (bool authorized) {
        return creator == self.protocolMultisig;
    }

    /// @notice Check if markings indicate protocol pool
    /// @param markings Pool markings
    /// @return isProtocol Whether this is a protocol pool
    function isProtocolPool(bytes3 markings) internal pure returns (bool isProtocol) {
        return (markings & PROTOCOL_POOL_FLAG) != 0;
    }
}

