// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

// Custom errors for gas-efficient reverts
error PoolManager__InsufficientOutput(uint256 minOut, uint256 actual);
error PoolManager__InvalidQuote();
error PoolManager__AtomicExecutionRequired();
error PoolManager__AccessDenied();
error PoolManager__OperationPaused();

import {ERC6909Claims} from "./ERC6909Claims.sol";
import {QuoteRouter} from "./QuoteRouter.sol";
import {PoolManagerLib} from "./libraries/PoolManagerLib.sol";
import {PoolIDAssembly} from "./libraries/PoolIDAssembly.sol";
import {SwapParams} from "./structs/SwapParams.sol";
import {Hop} from "./structs/Hop.sol";
import {PoolInfo} from "./structs/PoolInfo.sol";
import {ReentrancyGuard} from "./security/ReentrancyGuard.sol";
import {FlashAccounting} from "./libraries/FlashAccounting.sol";
import {IFlashCallback} from "./interfaces/internal/IFlashCallback.sol";
import {CommitRevealLib} from "./MEV/CommitRevealLib.sol";
import {AtomicExecutionLib} from "./MEV/AtomicExecutionLib.sol";
import {AccessControlLib} from "./MEV/AccessControlLib.sol";
import {SimpleGovernanceLib} from "./libraries/SimpleGovernanceLib.sol";
import {IPoolManager} from "./interfaces/internal/IPoolManager.sol";

/// @title PoolManager
/// @notice Core pool management contract optimized for size
/// @dev Liquidity management moved to external LiquidityManager contract
contract PoolManager is ERC6909Claims, QuoteRouter, ReentrancyGuard, IPoolManager {

    // Library storage for total liquidity tracking
    PoolManagerLib.PoolManagerStorage private _storage;

    // Address of external LiquidityManager contract
    address public liquidityManager;

    constructor(
        address _defaultData0Bridge
    ) QuoteRouter(_defaultData0Bridge) {
        // Initialize libraries with minimal setup
        AtomicExecutionLib.initializeDefaultConfigs(_storage.atomicData);
        AccessControlLib.initializeUniversalAccess(_storage.accessData);
    }

    /// @notice Set the liquidity manager address (one-time setup)
    function setLiquidityManager(address _liquidityManager) external {
        require(liquidityManager == address(0), "Already set");
        require(_liquidityManager != address(0), "Invalid address");
        liquidityManager = _liquidityManager;
    }

    /// @notice Creates a new pool
    function createPool(
        address asset0,
        address asset1,
        address quoter,
        bytes3 markings
    ) external nonReentrant returns (uint256 poolID) {
        poolID = PoolManagerLib.createPool(_storage, asset0, asset1, quoter, markings);

        if (SimpleGovernanceLib.isProtocolPool(markings)) {
            require(
                SimpleGovernanceLib.canCreateProtocolPool(_storage.simpleGovernance, msg.sender),
                "Only protocol"
            );
            SimpleGovernanceLib.registerProtocolPool(_storage.simpleGovernance, poolID);
        }
    }

    /// @notice Get pool information
    function getPoolInfo(uint256 poolID) external view returns (
        address asset0,
        address asset1,
        address quoter,
        bytes3 markings
    ) {
        PoolInfo memory poolInfo = PoolManagerLib.getPoolInfo(_storage, poolID);
        return (poolInfo.asset0, poolInfo.asset1, poolInfo.quoter, poolInfo.markings);
    }

    /// @notice Get total liquidity for a pool
    function totalLiquidity(uint256 poolID) external view override returns (uint256) {
        return _storage.totalLiquidity[poolID];
    }

    /// @notice Get current pool inventory
    function getInventory(uint256 poolId) external view override returns (uint128 asset0, uint128 asset1) {
        uint256 packed = _storage.poolInventories[poolId];
        asset0 = uint128(packed);
        asset1 = uint128(packed >> 128);
    }

    /// @notice Update inventory (internal helper)
    function _updateInventory(uint256 poolId, int128 asset0Delta, int128 asset1Delta) internal {
        uint256 packed = _storage.poolInventories[poolId];
        uint128 a0 = uint128(int128(uint128(packed)) + asset0Delta);
        uint128 a1 = uint128(int128(uint128(packed >> 128)) + asset1Delta);
        _storage.poolInventories[poolId] = uint256(a0) | (uint256(a1) << 128);
    }

    /// @notice Swap assets in a pool - CORE FUNCTION (kept internal for gas efficiency)
    function swap(
        address asset0,
        address asset1,
        address quoter,
        bytes3 markings,
        uint256 amountIn,
        bool zeroForOne,
        uint256 minAmountOut
    ) external payable nonReentrant returns (uint256 amountOut) {
        return _executeSwap(asset0, asset1, quoter, markings, amountIn, zeroForOne, minAmountOut, 0x00000000, "");
    }

    /// @notice Swap with trader protection
    function swapWithProtection(
        address asset0,
        address asset1,
        address quoter,
        bytes3 markings,
        uint256 amountIn,
        bool zeroForOne,
        uint256 minAmountOut,
        bytes4 traderProtection
    ) external payable nonReentrant returns (uint256 amountOut) {
        return _executeSwap(asset0, asset1, quoter, markings, amountIn, zeroForOne, minAmountOut, traderProtection, "");
    }

    /// @notice Multi-hop batch swap
    function batchSwap(
        Hop[] calldata hops,
        uint256 amountIn,
        uint256 minAmountOut
    ) external payable nonReentrant returns (uint256 amountOut) {
        address recipient = FlashAccounting.getActiveUser();
        if (recipient == address(0)) recipient = msg.sender;

        amountOut = PoolManagerLib.executeBatchSwap(_storage, hops, amountIn, msg.value, recipient, address(this));

        if (amountOut < minAmountOut) revert PoolManager__InsufficientOutput(minAmountOut, amountOut);
    }

    /// @notice Flash session for composable operations
    function flashSession(address callback, bytes calldata data, address[] calldata tokens) external payable nonReentrantKey(keccak256("flashSession")) {
        FlashAccounting.startSession(msg.sender);
        FlashAccounting.setActiveUser(msg.sender);
        IFlashCallback(callback).flashCallback(data);
        PoolManagerLib.settleUserTokens(msg.sender, tokens, msg.value);
        FlashAccounting.clearActiveUser();
        FlashAccounting.endSession(msg.sender);
    }

    /// @notice Get user deltas
    function getUserDeltas(address user, address[] calldata tokens) public view returns (int256[] memory deltas) {
        deltas = new int256[](tokens.length);
        for (uint256 i = 0; i < tokens.length; ) {
            deltas[i] = FlashAccounting.getDelta(user, tokens[i]);
            unchecked { ++i; }
        }
    }

    /// @notice Expose quote function for external contracts
    function routerGetQuote(
        SwapParams memory p,
        uint128 asset0Balance,
        uint128 asset1Balance
    ) public override returns (uint256 quote, uint256 poolID) {
        return QuoteRouter.getQuote(p, asset0Balance, asset1Balance);
    }

    /// @notice Execute liquidity addition (called by LiquidityManager)
    function executeLiquidityAdd(
        uint256 poolID,
        address asset0,
        address asset1,
        uint256 amount0,
        uint256 amount1,
        uint256 liquidity,
        address user,
        uint256 msgValue
    ) external override returns (uint256 actualLiquidity) {
        require(msg.sender == liquidityManager, "Only LiquidityManager");

        // Determine beneficiary
        address beneficiary = FlashAccounting.getActiveUser();
        if (beneficiary == address(0)) beneficiary = user;

        // Flash accounting: record user owes tokens
        FlashAccounting.addDelta(beneficiary, asset0, -int256(amount0));
        FlashAccounting.addDelta(beneficiary, asset1, -int256(amount1));

        // Update inventory
        _updateInventory(poolID, int128(uint128(amount0)), int128(uint128(amount1)));

        // Mint shares and update total
        _mint(beneficiary, poolID, liquidity);
        _storage.totalLiquidity[poolID] += liquidity;

        // Settle if not in session
        if (!FlashAccounting.isSessionActive(beneficiary)) {
            address[] memory tokens = new address[](2);
            tokens[0] = asset0;
            tokens[1] = asset1;
            PoolManagerLib.settleUserTokens(beneficiary, tokens, msgValue);
        }

        return liquidity;
    }

    /// @notice Execute liquidity removal (called by LiquidityManager)  
    function executeLiquidityRemove(
        uint256 poolID,
        address asset0,
        address asset1,
        uint256 amount0,
        uint256 amount1,
        uint256 liquidity,
        address user
    ) external override returns (uint256, uint256) {
        require(msg.sender == liquidityManager, "Only LiquidityManager");

        address beneficiary = FlashAccounting.getActiveUser();
        if (beneficiary == address(0)) beneficiary = user;

        // Burn shares and update total
        _burn(beneficiary, poolID, liquidity);
        _storage.totalLiquidity[poolID] -= liquidity;

        // Update inventory
        _updateInventory(poolID, -int128(uint128(amount0)), -int128(uint128(amount1)));

        // Flash accounting: record user is owed tokens
        FlashAccounting.addDelta(beneficiary, asset0, int256(amount0));
        FlashAccounting.addDelta(beneficiary, asset1, int256(amount1));

        // Settle if not in session
        if (!FlashAccounting.isSessionActive(beneficiary)) {
            address[] memory tokens = new address[](2);
            tokens[0] = asset0;
            tokens[1] = asset1;
            PoolManagerLib.settleUserTokens(beneficiary, tokens, 0);
        }

        return (amount0, amount1);
    }

    /// @notice Internal swap execution - OPTIMIZED FOR FREQUENT USE
    function _executeSwap(
        address asset0,
        address asset1,
        address quoter,
        bytes3 markings,
        uint256 amountIn,
        bool zeroForOne,
        uint256 minAmountOut,
        bytes4 traderProtection,
        bytes memory permitData
    ) internal returns (uint256 amountOut) {
        address beneficiary = FlashAccounting.getActiveUser();
        if (beneficiary == address(0)) beneficiary = msg.sender;

        // Validate requirements (optimized checks)
        if ((uint32(traderProtection) & 0x00000100) != 0) {
            if (!AtomicExecutionLib.validateAtomicExecution(
                uint32(traderProtection), 
                FlashAccounting.isSessionActive(beneficiary), 
                _storage.atomicData
            )) {
                revert PoolManager__AtomicExecutionRequired();
            }
        }

        if ((markings & 0x000002) != 0) {
            if (!AccessControlLib.validateUniversalAccess(
                markings, uint32(traderProtection), _storage.accessData, beneficiary, permitData, 0
            )) {
                revert PoolManager__AccessDenied();
            }
        }

        // Calculate poolID and validate governance
        uint256 poolID = PoolIDAssembly.assemblePoolID(asset0, asset1, quoter, markings);

        if ((uint24(markings) & uint24(SimpleGovernanceLib.PROTOCOL_POOL_FLAG)) != 0) {
            uint256 packed = _storage.simpleGovernance.globalPackedState;
            if (uint8(packed) != 0) revert PoolManager__OperationPaused();
            if (uint32((packed >> 8) & 0xFFFFFF) != 0) {
                if (_storage.simpleGovernance.poolStates[poolID] != 0) revert PoolManager__OperationPaused();
            }
        }

        // Get inventory and calculate output
        (uint128 poolAsset0, uint128 poolAsset1) = this.getInventory(poolID);

        SwapParams memory swapParams = SwapParams({
            asset0: asset0,
            asset1: asset1,
            quoter: quoter,
            amount: new uint256[](1),
            zeroForOne: zeroForOne,
            marking: new bytes3[](1),
            traderProtection: traderProtection
        });
        swapParams.amount[0] = amountIn;
        swapParams.marking[0] = markings;

        (uint256 quote, ) = getQuote(swapParams, poolAsset0, poolAsset1);
        if (quote == 0) revert PoolManager__InvalidQuote();
        amountOut = quote;

        PoolManagerLib.validateSwapInventory(poolAsset0, poolAsset1, amountOut, zeroForOne);
        if (amountOut < minAmountOut) revert PoolManager__InsufficientOutput(minAmountOut, amountOut);

        // Update inventory in canonical order
        bool canonicalOrder = asset0 < asset1;
        bool canonicalZeroForOne = canonicalOrder ? zeroForOne : !zeroForOne;
        _updateInventory(
            poolID,
            canonicalZeroForOne ? int128(uint128(amountIn)) : -int128(uint128(amountOut)),
            canonicalZeroForOne ? -int128(uint128(amountOut)) : int128(uint128(amountIn))
        );

        // Flash accounting
        address inAsset = zeroForOne ? asset0 : asset1;
        address outAsset = zeroForOne ? asset1 : asset0;
        FlashAccounting.addDelta(beneficiary, inAsset, -int256(amountIn));
        FlashAccounting.addDelta(beneficiary, outAsset, int256(amountOut));

        if (!FlashAccounting.isSessionActive(beneficiary)) {
            PoolManagerLib._settleUserDeltas(beneficiary, inAsset, outAsset, 0, 0, msg.value);
        }
    }

    /// @notice Configure protocol fee
    function configureProtocolFee(address treasury, uint16 feeBps) external {
        PoolManagerLib.configureProtocolFee(_storage, treasury, feeBps);
    }

    /// @notice Commit-reveal functions (kept minimal)
    function commitSwap(bytes32 commitment) external {
        CommitRevealLib.storeCommitment(_storage.commitData, commitment, msg.sender);
    }

    function executeCommittedSwap(
        address asset0,
        address asset1,
        address quoter,
        bytes3 markings,
        uint256 amountIn,
        bool zeroForOne,
        uint256 minAmountOut,
        uint64 nonce,
        bytes32 salt
    ) external payable nonReentrant returns (uint256 amountOut) {
        bytes32 commitment = CommitRevealLib.generateCommitment(
            asset0, asset1, quoter, markings, amountIn, zeroForOne, minAmountOut, nonce, msg.sender, salt
        );
        CommitRevealLib.validateAndConsumeCommitment(_storage.commitData, commitment, msg.sender, nonce);
        return _executeSwap(asset0, asset1, quoter, markings, amountIn, zeroForOne, minAmountOut, 0x00000000, "");
    }

    function getCommitNonce(address trader) external view returns (uint64 nonce) {
        return CommitRevealLib.getCurrentNonce(_storage.commitData, trader);
    }

    /// @notice Governance functions (minimal)
    function setGovernance(address protocol, address emergency) external {
        require(_storage.simpleGovernance.protocolMultisig == address(0), "Already set");
        require(protocol != address(0) && emergency != address(0), "Invalid addresses");
        _storage.simpleGovernance.protocolMultisig = protocol;
        _storage.simpleGovernance.emergencyMultisig = emergency;
    }

    function emergencyPauseProtocolPools(uint8 newState) external {
        require(
            msg.sender == _storage.simpleGovernance.emergencyMultisig || 
            msg.sender == _storage.simpleGovernance.protocolMultisig, 
            "Only emergency"
        );
        SimpleGovernanceLib.setGlobalEmergencyState(_storage.simpleGovernance, newState);
    }

    function setDataBridge(uint8 overallSlot, address bridge) external {
        require(msg.sender == _storage.simpleGovernance.protocolMultisig, "Only protocol");
        require(overallSlot >= 4 && overallSlot <= 15, "Invalid slot");
        _setConfigurableDataBridge(overallSlot - 4, bridge);
    }
}

contracts/Core/QuoteRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {MarkingHelper} from "./libraries/MarkingHelper.sol";
import {PoolIDAssembly} from "./libraries/PoolIDAssembly.sol";
import {TransientStorage} from "./libraries/TransientStorage.sol";

import {IQuoter} from "./interfaces/internal/quoters/IQuoter.sol";
import {IDataBridge} from "./interfaces/internal/IDataBridge.sol";

import {SwapParams} from "./structs/SwapParams.sol";
import {Markings} from "./structs/Markings.sol"; 
import {QuoteParams, QuoteParamsBatch} from "./structs/QuoteParams.sol";
import {TraderContext, QuoteParamsWithContext, QuoteParamsBatchWithContext} from "./structs/TraderContext.sol";
import {FlashAccounting} from "./libraries/FlashAccounting.sol";

abstract contract QuoteRouter {
    using TransientStorage for address;

    // Enhanced context flag in markings (bit 0)
    bytes3 constant ENHANCED_CONTEXT_FLAG = 0x000001;

    address immutable public defaultData0Bridge;

    // 12 configurable data bridges (slots 4..15 overall). These are storage-based and can be updated post-deploy.
    // Index mapping:
    // - Overall slots 0..3 map to immutables defaultData0..3 (future expansion)
    // - Overall slots 4..14 map to configurableDataBridges[0..10] 
    // - Overall slot 15 is special consolidated bridge slot (dx) - no caching, uses defaultData0Bridge
    address[12] public configurableDataBridges;

    constructor(
        address _defaultData0Bridge
    ) {
        defaultData0Bridge = _defaultData0Bridge;
    }

    // Internal setter to be called by inheritors (e.g., PoolManager) with proper access control
    function _setConfigurableDataBridge(uint8 slotIndex, address bridge) internal {
        // slotIndex: 0..11 corresponding to overall slots 4..15
        require(slotIndex < 12, "Invalid slot");
        configurableDataBridges[slotIndex] = bridge;
    }

    function _getMarketDataCached(address dataAddress, QuoteParams memory params) internal returns (bytes memory data) {
        if (dataAddress == address(0)) {
            return bytes("");
        }
        // Skip non-contract addresses entirely
        if (dataAddress.code.length == 0) {
            return bytes("");
        }
        // Transient cache: check first
        data = dataAddress.loadTransient();
        bool hit = data.length > 0;
        if (!hit) {
            // Fetch and cache
            try IDataBridge(dataAddress).getData(params) returns (bytes memory fetched) {
                data = fetched;
                if (data.length > 0) {
                    dataAddress.storeTransient(data);
                }
            } catch {
                data = bytes("");
            }
        }
        return data;
    }

    function _getMarketDataNoCache(address dataAddress, QuoteParams memory params) internal returns (bytes memory data) {
        if (dataAddress == address(0)) return bytes("");
        if (dataAddress.code.length == 0) return bytes("");
        try IDataBridge(dataAddress).getData(params) returns (bytes memory fetched) {
            return fetched;
        } catch {
            return bytes("");
        }
    }



    // Made public to allow library calls via PoolManager address
    function getQuote(SwapParams memory p, uint128 asset0Balance, uint128 asset1Balance) public returns (uint256 quote, uint256 poolID) {
        poolID = PoolIDAssembly.assemblePoolID(p.asset0, p.asset1, p.quoter, p.marking[0]);
        Markings memory m = MarkingHelper.decodeMarkings(p.marking[0]);

        QuoteParams memory params = QuoteParams({
            asset0: p.asset0,
            asset1: p.asset1,
            quoter: p.quoter,
            amount: p.amount[0],
            asset0Balance: asset0Balance,
            asset1Balance: asset1Balance,
            bucketID: m.bucketID,
            zeroForOne: p.zeroForOne,
            functionFlags: _inferFunctionFlags(m)
        });
        // Efficiently build payload from enabled bridges only
        // Decode bridge flags: extraBridgeSlot can be:
        // - 0: no extra bridges
        // - 1-14: single configurable bridge at that slot (slots 4-14)
        // - 15: special consolidated bridge slot (dx) - uses defaultData0Bridge, no caching

        bytes memory d0 = bytes(""); // Reserved for future use (slots 0-3)
        bytes memory d1 = bytes("");
        bytes memory d2 = bytes("");
        bytes memory d3raw = bytes("");

        // Handle configurable bridges and special slot 16
        bytes memory dx;
        if (m.extraBridgeSlot == 0) {
            // No extra bridges
            dx = bytes("");
        } else if (m.extraBridgeSlot <= 14) {
            // Single configurable bridge at specified slot (4-14)
            uint8 slotIndex = m.extraBridgeSlot - 4;
            if (m.extraBridgeSlot >= 4 && slotIndex < 11) {
                address bridge = configurableDataBridges[slotIndex];
                dx = (bridge != address(0)) ? _getMarketDataNoCache(bridge, params) : bytes("");
            } else {
                dx = bytes("");
            }
        } else if (m.extraBridgeSlot == 15) {
            // Special slot 15: consolidated bridge (dx) - no caching, uses defaultData0Bridge
            dx = _getMarketDataNoCache(defaultData0Bridge, params);
        } else {
            // Reserved for future expansion
            dx = bytes("");
        }
        // Always include a context slot in d3 as (bytes d3raw, bytes contextBytes)
        bytes memory contextBytes;
        if ((p.marking[0] & ENHANCED_CONTEXT_FLAG) != 0) {
            TraderContext memory context = TraderContext({
                trader: msg.sender,
                txOrigin: tx.origin,
                blockNumber: block.number,
                timestamp: block.timestamp,
                gasPrice: tx.gasprice,
                gasLeft: gasleft(),
                sessionActive: FlashAccounting.isSessionActive(msg.sender)
            });
            contextBytes = abi.encode(context);
        } else {
            contextBytes = bytes("");
        }
        bytes memory d3 = abi.encode(d3raw, contextBytes);
        // Extend routed payload to include dx as fifth element for future-proofing
        bytes memory routed = abi.encode(d0, d1, d2, d3, dx);
        quote = IQuoter(params.quoter).quote(params, routed);
    }

    function getQuoteBatch(SwapParams memory p, uint128[] memory asset0Balances, uint128[] memory asset1Balances) public returns (uint256[] memory quote, uint256[] memory poolID) {
        poolID = new uint256[](p.marking.length);
        quote = new uint256[](p.marking.length);
        uint16[] memory bucketIDs = new uint16[](p.marking.length);
        Markings memory m = MarkingHelper.decodeMarkings(p.marking[0]);
        for (uint i = 0; i < p.marking.length; ) {
            bytes3 mark = p.marking[i];
            poolID[i] = PoolIDAssembly.assemblePoolID(p.asset0, p.asset1, p.quoter, mark);
            bucketIDs[i] = MarkingHelper.decodeMarkings(mark).bucketID;
            unchecked { ++i; }
        }
        QuoteParamsBatch memory params = QuoteParamsBatch({
            asset0: p.asset0,
            asset1: p.asset1,
            quoter: p.quoter,
            amount: p.amount,
            asset0Balances: asset0Balances,
            asset1Balances: asset1Balances,
            bucketID: bucketIDs,
            zeroForOne: p.zeroForOne,
            functionFlags: _inferFunctionFlags(m)
        });
        // For batch, fetch once per marking set; cached fetches to enable hits across hops
        QuoteParams memory baseParams = QuoteParams({
            asset0: p.asset0,
            asset1: p.asset1,
            quoter: p.quoter,
            amount: 0,
            asset0Balance: 0,
            asset1Balance: 0,
            bucketID: 0,
            zeroForOne: p.zeroForOne,
            functionFlags: 0
        });
        // Efficiently build payload with caching for batch operations
        // For StoixDataBridge architecture: put consolidated bridge data in dx, not d0
        bytes memory d0 = bytes("");
        bytes memory d1 = bytes("");
        bytes memory d2 = bytes("");
        bytes memory d3raw = bytes("");

        // Handle configurable bridges and special slot 16 with caching
        bytes memory dx;
        if (m.extraBridgeSlot == 0) {
            // No extra bridges
            dx = bytes("");
        } else if (m.extraBridgeSlot <= 14) {
            // Single configurable bridge at specified slot (4-14)
            uint8 slotIndex = m.extraBridgeSlot - 4;
            if (m.extraBridgeSlot >= 4 && slotIndex < 11) {
                address bridge = configurableDataBridges[slotIndex];
                dx = (bridge != address(0)) ? _getMarketDataCached(bridge, baseParams) : bytes("");
            } else {
                dx = bytes("");
            }
        } else if (m.extraBridgeSlot == 15) {
            // Special slot 15: consolidated bridge (dx) - no caching, uses defaultData0Bridge
            dx = _getMarketDataNoCache(defaultData0Bridge, baseParams);
        } else {
            // Reserved for future expansion
            dx = bytes("");
        }
        // Always include a context slot in d3 as (bytes d3raw, bytes contextBytes)
        bool needsContext = false;
        for (uint i = 0; i < p.marking.length; ) {
            if ((p.marking[i] & ENHANCED_CONTEXT_FLAG) != 0) { needsContext = true; break; }
            unchecked { ++i; }
        }
        bytes memory contextBytes;
        if (needsContext) {
            TraderContext memory context = TraderContext({
                trader: msg.sender,
                txOrigin: tx.origin,
                blockNumber: block.number,
                timestamp: block.timestamp,
                gasPrice: tx.gasprice,
                gasLeft: gasleft(),
                sessionActive: FlashAccounting.isSessionActive(msg.sender)
            });
            contextBytes = abi.encode(context);
        } else {
            contextBytes = bytes("");
        }
        bytes memory d3 = abi.encode(d3raw, contextBytes);
        bytes memory routed = abi.encode(d0, d1, d2, d3, dx);
        quote = IQuoter(p.quoter).quoteBatch(params, routed);

    }

    // Helper to check if bytes is (bytes,bytes)
    function _decodeTwoBytes(bytes memory data) external pure returns (bytes memory a, bytes memory b) {
        (a, b) = abi.decode(data, (bytes, bytes));
    }

    /*//////////////////////////////////////////////////////////////
                        ENHANCED CONTEXT QUOTES
    //////////////////////////////////////////////////////////////*/

    // Removed explicit WithContext path in favor of embedding context into routed bytes for standard IQuoter

    // Removed explicit BatchWithContext; we'll embed context into routed bytes in auto path

    /*//////////////////////////////////////////////////////////////
                        CONTEXT ROUTING LOGIC
    //////////////////////////////////////////////////////////////*/

    /// @notice Smart routing that chooses standard vs enhanced context based on markings
    /// @dev Automatically detects if enhanced context is needed and routes accordingly
    function getQuoteAuto(
        SwapParams memory p, 
        uint128 asset0Balance, 
        uint128 asset1Balance,
        address trader
    ) public returns (uint256 quote, uint256 poolID) {
        // Check if enhanced context is required; if so, embed it in d3 and use standard IQuoter
        if ((p.marking[0] & ENHANCED_CONTEXT_FLAG) != 0) {
            poolID = PoolIDAssembly.assemblePoolID(p.asset0, p.asset1, p.quoter, p.marking[0]);
            Markings memory m = MarkingHelper.decodeMarkings(p.marking[0]);

            // Build trader context bytes (encode struct as bytes)
            TraderContext memory context = TraderContext({
                trader: trader,
                txOrigin: tx.origin,
                blockNumber: block.number,
                timestamp: block.timestamp,
                gasPrice: tx.gasprice,
                gasLeft: gasleft(),
                sessionActive: FlashAccounting.isSessionActive(trader)
            });
            bytes memory contextBytes = abi.encode(context);

            // Build payload from up to 4 default bridges, uncached for single swap
            QuoteParams memory qp = QuoteParams({
                asset0: p.asset0,
                asset1: p.asset1,
                quoter: p.quoter,
                amount: p.amount[0],
                asset0Balance: asset0Balance,
                asset1Balance: asset1Balance,
                bucketID: m.bucketID,
                zeroForOne: p.zeroForOne,
                functionFlags: _inferFunctionFlags(m)
            });
            // For StoixDataBridge architecture with context: put bridge data in dx (5th position)
            bytes memory d0 = bytes("");
            bytes memory d1 = bytes("");
            bytes memory d2 = bytes("");
            bytes memory d3raw = bytes("");
            bytes memory dx = m.data0 ? _getMarketDataNoCache(defaultData0Bridge, qp) : bytes("");
            // Combine d3 payload with context: (bytes d3raw, bytes contextBytes)
            bytes memory d3 = abi.encode(d3raw, contextBytes);
            bytes memory routed = abi.encode(d0, d1, d2, d3, dx);
            quote = IQuoter(qp.quoter).quote(qp, routed);
            return (quote, poolID);
        }
        return getQuote(p, asset0Balance, asset1Balance);
    }

    /// @notice Smart batch routing that chooses standard vs enhanced context based on markings
    /// @dev Automatically detects if enhanced context is needed and routes accordingly
    function getQuoteBatchAuto(
        SwapParams memory p, 
        uint128[] memory asset0Balances, 
        uint128[] memory asset1Balances,
        address trader
    ) public returns (uint256[] memory quote, uint256[] memory poolID) {
        // Check if any marking requires enhanced context
        bool needsContext = false;
        for (uint i = 0; i < p.marking.length; ) {
            if ((p.marking[i] & ENHANCED_CONTEXT_FLAG) != 0) {
                needsContext = true;
                break;
            }
            unchecked { ++i; }
        }
        if (!needsContext) {
            return getQuoteBatch(p, asset0Balances, asset1Balances);
        }
        poolID = new uint256[](p.marking.length);
        uint16[] memory bucketIDs = new uint16[](p.marking.length);
        for (uint i = 0; i < p.marking.length; ) {
            bytes3 mark = p.marking[i];
            poolID[i] = PoolIDAssembly.assemblePoolID(p.asset0, p.asset1, p.quoter, mark);
            bucketIDs[i] = MarkingHelper.decodeMarkings(mark).bucketID;
            unchecked { ++i; }
        }
        // Build trader context bytes
        TraderContext memory context = TraderContext({
            trader: trader,
            txOrigin: tx.origin,
            blockNumber: block.number,
            timestamp: block.timestamp,
            gasPrice: tx.gasprice,
            gasLeft: gasleft(),
            sessionActive: FlashAccounting.isSessionActive(trader)
        });
        bytes memory contextBytes = abi.encode(context);
        // For batch, fetch once per marking set; cached fetches to enable hits across hops
        Markings memory m0 = MarkingHelper.decodeMarkings(p.marking[0]);
        QuoteParams memory baseParams = QuoteParams({
            asset0: p.asset0,
            asset1: p.asset1,
            quoter: p.quoter,
            amount: 0,
            asset0Balance: 0,
            asset1Balance: 0,
            bucketID: 0,
            zeroForOne: p.zeroForOne,
            functionFlags: _inferFunctionFlags(m0)
        });
        // For StoixDataBridge architecture with context batch: put bridge data in dx (5th position)
        bytes memory d0 = bytes("");
        bytes memory d1 = bytes("");
        bytes memory d2 = bytes("");
        bytes memory d3raw = bytes("");
        bytes memory dx = m0.data0 ? _getMarketDataCached(defaultData0Bridge, baseParams) : bytes("");
        // Combine d3 payload with context once for batch
        bytes memory d3 = abi.encode(d3raw, contextBytes);
        bytes memory routed = abi.encode(d0, d1, d2, d3, dx);
        QuoteParamsBatch memory params = QuoteParamsBatch({
            asset0: p.asset0,
            asset1: p.asset1,
            quoter: p.quoter,
            amount: p.amount,
            asset0Balances: asset0Balances,
            asset1Balances: asset1Balances,
            bucketID: bucketIDs,
            zeroForOne: p.zeroForOne,
            functionFlags: 0
        });
        quote = IQuoter(p.quoter).quoteBatch(params, routed);
    }

    // Map markings to StoixBridge function flags. Use slot 15 for consolidated bridge.
    function _inferFunctionFlags(Markings memory m) internal pure returns (uint8) {
        // If slot 15 used (consolidated StoixBridge), use bucketID lower 8 bits as function flags.
        // Default to 0x0F (v2|v3|twap|cl) when override is zero.
        if (m.extraBridgeSlot != 15) return 0;
        uint8 overrideFlags = uint8(m.bucketID & 0xFF);
        return overrideFlags == 0 ? 0x0F : overrideFlags;
    }
}

contracts/Core/security/ReentrancyGuard..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @title ReentrancyGuard
/// @notice Reentrancy guard implemented with EIP-1153 transient storage
abstract contract ReentrancyGuard {
    // Base domain separator for guard slots
    bytes32 private constant _REENTRANCY_GUARD_DOMAIN = keccak256("dex.reentrancy.guard:v1");

    modifier nonReentrant() {
        _enterReentrancyGuard(_globalGuardSlot());
        _;
        _exitReentrancyGuard(_globalGuardSlot());
    }

    modifier nonReentrantKey(bytes32 key) {
        bytes32 slot = _keyedGuardSlot(key);
        _enterReentrancyGuard(slot);
        _;
        _exitReentrancyGuard(slot);
    }

    function _globalGuardSlot() internal view returns (bytes32 slot) {
        slot = keccak256(abi.encodePacked(_REENTRANCY_GUARD_DOMAIN, address(this)));
    }

    function _keyedGuardSlot(bytes32 key) internal view returns (bytes32 slot) {
        slot = keccak256(abi.encodePacked(_REENTRANCY_GUARD_DOMAIN, address(this), key));
    }

    function _enterReentrancyGuard(bytes32 slot) private {
        assembly {
            if tload(slot) { revert(0, 0) }
            tstore(slot, 1)
        }
    }

    function _exitReentrancyGuard(bytes32 slot) private {
        assembly {
            tstore(slot, 0)
        }
    }
}

