contracts/Core/libraries/AssetTransferLi.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

library AssetTransferLib {
    error AssetTransferLib__IncorrectEthSent();
    error AssetTransferLib__EthNotExpected();
    error AssetTransferLib__Erc20TransferFailed();
    /// @notice Handles transfer of ERC20 or ETH from user to contract
    /// @param asset The asset address (address(0) for ETH)
    /// @param from The sender address
    /// @param amount The amount to transfer
    /// @param msgValue The msg.value passed to the function (for ETH validation)
    function transferIn(address asset, address from, uint256 amount, uint256 msgValue) internal {
        if (asset == address(0)) {
            // ETH
            if (msgValue != amount) revert AssetTransferLib__IncorrectEthSent();
        } else {
            // ERC20
            if (msgValue != 0) revert AssetTransferLib__EthNotExpected();
            if (!IERC20(asset).transferFrom(from, address(this), amount)) revert AssetTransferLib__Erc20TransferFailed();
        }
    }

    /// @notice Handles transfer of ERC20 or ETH from contract to recipient
    /// @param asset The asset address (address(0) for ETH)
    /// @param recipient The recipient address
    /// @param amount The amount to transfer
    function transferOut(address asset, address recipient, uint256 amount) internal {
        if (asset == address(0)) {
            // ETH
            payable(recipient).transfer(amount);
        } else {
            // ERC20
            if (!IERC20(asset).transfer(recipient, amount)) revert AssetTransferLib__Erc20TransferFailed();
        }
    }
}

contracts/Core/libraries/FlashAccounting.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @title FlashAccounting
/// @notice Minimal transient-storage based per-session ledger for user/token deltas
library FlashAccounting {
    bytes32 private constant FLASH_DOMAIN = keccak256("dex.flash.accounting:v1");
    bytes32 private constant FLASH_SESSION = keccak256("dex.flash.session:v1");
    bytes32 private constant FLASH_ACTIVE_USER = keccak256("dex.flash.active.user:v1");

    function _slot(address callerContract, address user, address token) private pure returns (bytes32 slot) {
        // Unique slot per (contract, user, token)
        slot = keccak256(abi.encodePacked(FLASH_DOMAIN, callerContract, user, token));
    }

    /// @notice Add to the user's net delta for a given token
    /// @dev Negative delta means the user owes token; positive means the user is owed token
    function addDelta(address user, address token, int256 delta) internal {
        bytes32 slot = _slot(address(this), user, token);
        assembly {
            let old := tload(slot)
            let new := add(old, delta)
            tstore(slot, new)
        }
    }

    /// @notice Get the user's current net delta for a given token
    function getDelta(address user, address token) internal view returns (int256 current) {
        bytes32 slot = _slot(address(this), user, token);
        assembly {
            current := tload(slot)
        }
    }

    /// @notice Clear the user's delta for a given token
    function clearDelta(address user, address token) internal {
        bytes32 slot = _slot(address(this), user, token);
        assembly {
            tstore(slot, 0)
        }
    }

    function _sessionSlot(address callerContract, address user) private pure returns (bytes32 slot) {
        slot = keccak256(abi.encodePacked(FLASH_SESSION, callerContract, user));
    }

    function startSession(address user) internal {
        bytes32 slot = _sessionSlot(address(this), user);
        assembly {
            tstore(slot, 1)
        }
    }

    function endSession(address user) internal {
        bytes32 slot = _sessionSlot(address(this), user);
        assembly {
            tstore(slot, 0)
        }
    }

    function isSessionActive(address user) internal view returns (bool active) {
        bytes32 slot = _sessionSlot(address(this), user);
        assembly {
            active := tload(slot)
        }
    }

    function setActiveUser(address user) internal {
        bytes32 slot = keccak256(abi.encodePacked(FLASH_ACTIVE_USER, address(this)));
        assembly {
            tstore(slot, user)
        }
    }

    function clearActiveUser() internal {
        bytes32 slot = keccak256(abi.encodePacked(FLASH_ACTIVE_USER, address(this)));
        assembly {
            tstore(slot, 0)
        }
    }

    function getActiveUser() internal view returns (address user) {
        bytes32 slot = keccak256(abi.encodePacked(FLASH_ACTIVE_USER, address(this)));
        assembly {
            user := tload(slot)
        }
    }
}

contracts/Core/libraries/MarkingHelper.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;
import {Markings} from "../structs/Markings.sol";

library MarkingHelper {
    // New simple bit layout: [bucketID (12 bits)] [data3 (1)] [data2 (1)] [data1 (1)] [data0 (1)]
    // We decode into booleans for data0..data3 and bucketID
    function decodeMarkings(bytes3 markings) internal pure returns (Markings memory result) {
        uint24 data = uint24(markings);
        result.data0 = (data & 0x1) != 0;
        result.data1 = ((data >> 1) & 0x1) != 0;
        result.data2 = ((data >> 2) & 0x1) != 0;
        result.data3 = ((data >> 3) & 0x1) != 0;
        result.bucketID = uint16((data >> 4) & 0xFFF);
        return result;
    }
}

