// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {IDataBridge} from "../../../Core/interfaces/internal/IDataBridge.sol";
import {QuoteParams} from "../../../Core/structs/QuoteParams.sol";
import {AggregatorV3Interface} from "../../../Core/interfaces/external/IChainlinkAggregator.sol";

/// @notice Chainlink-based bridge using USD feeds for asset0 and asset1
contract ChainlinkDataBridge is IDataBridge {
    address public immutable asset0UsdFeed;
    address public immutable asset1UsdFeed;

    constructor(address _asset0UsdFeed, address _asset1UsdFeed) {
        asset0UsdFeed = _asset0UsdFeed;
        asset1UsdFeed = _asset1UsdFeed;
    }

    function getData(QuoteParams memory) external view override returns (bytes memory) {
        // Read Chainlink answers
        (uint256 p0, uint8 d0) = _latestPriceAndDecimals(asset0UsdFeed);
        (uint256 p1, uint8 d1) = _latestPriceAndDecimals(asset1UsdFeed);
        // spot = (p0 / 10^d0) / (p1 / 10^d1) scaled to 1e18
        // => p0 * 10^(18 + d1) / (p1 * 10^d0)
        uint256 num = p0 * (10 ** (18 + d1));
        uint256 den = p1 * (10 ** d0);
        require(den != 0, "den=0");
        uint256 spot = num / den;
        // For TWAP we mirror spot (Chainlink already aggregates over time)
        uint256 twap = spot;
        return abi.encode(spot, twap);
    }

    function _latestPriceAndDecimals(address feed) internal view returns (uint256, uint8) {
        AggregatorV3Interface a = AggregatorV3Interface(feed);
        (, int256 answer,, uint256 updatedAt,) = a.latestRoundData();
        require(answer > 0, "bad answer");
        require(updatedAt != 0, "stale");
        return (uint256(answer), a.decimals());
    }
}
