contracts/TestTransientStorage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {TransientStorage} from "./libraries/TransientStorage.sol";

contract TestTransientStorage {
    using TransientStorage for address;

    /// @notice Test basic tstore/tload operations
    function testBasicTransientStorage() external returns (uint256 value) {
        assembly {
            // Store a simple value
            tstore(0x1, 42)
            // Load it back
            value := tload(0x1)
        }
    }

    /// @notice Test storing and loading a hash
    function testHashStorage(bytes memory data) external returns (bytes32 storedHash, bytes32 loadedHash) {
        assembly {
            // Calculate hash
            storedHash := keccak256(add(data, 0x20), mload(data))
            // Store hash in transient storage
            tstore(0x2, storedHash)
            // Load it back
            loadedHash := tload(0x2)
        }
    }

    /// @notice Test multiple tstore operations
    function testMultipleStores() external returns (uint256 val1, uint256 val2, uint256 val3) {
        assembly {
            // Store multiple values
            tstore(0x10, 100)
            tstore(0x20, 200) 
            tstore(0x30, 300)

            // Load them back
            val1 := tload(0x10)
            val2 := tload(0x20)
            val3 := tload(0x30)
        }
    }

    /// @notice Test that transient storage clears between transactions
    function getStoredValue() external view returns (uint256 value) {
        assembly {
            value := tload(0x1)
        }
    }

    /// @notice Test address-based storage slots
    function testAddressBasedSlots(address addr) external returns (uint256 stored, uint256 loaded) {
        assembly {
            stored := 999
            tstore(addr, stored)
            loaded := tload(addr)
        }
    }

    /// @notice Test the full TransientStorage library with bytes data
    function testTransientStorageLibrary(bytes memory testData) external returns (bytes memory storedData, bytes memory loadedData) {
        address key = address(0x1234567890123456789012345678901234567890);

        // Store data using our library
        key.storeTransient(testData);
        storedData = testData;

        // Load data using our library
        loadedData = key.loadTransient();
    }

    /// @notice Test caching behavior (store once, load multiple times)
    function testCachingBehavior(bytes memory data) external returns (
        bytes memory firstLoad,
        bytes memory secondLoad,
        bool matches
    ) {
        address key = address(0x9999999999999999999999999999999999999999);

        // Store data
        key.storeTransient(data);

        // Load twice
        firstLoad = key.loadTransient();
        secondLoad = key.loadTransient();

        // Check if they match
        matches = keccak256(firstLoad) == keccak256(secondLoad) && 
                 keccak256(firstLoad) == keccak256(data);
    }
}

