// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {IQuoterDualData} from "../interfaces/internal/quoters/IQuoterDualData.sol";
import {QuoteParams, QuoteParamsBatch} from "../structs/QuoteParams.sol";

/// @title DualDataQuoter
/// @notice A quoter that requires both alpha and beta market data for sophisticated pricing
/// @dev This is a skeleton implementation with dummy logic - real pricing logic will be added later
contract DualDataQuoter is IQuoterDualData {
    // execution cost around 16,000 gas
    /// @notice Calculate quote using both alpha and beta market data
    /// @param params Quote parameters containing asset balances and swap amount
    /// @param alphaData Encoded alpha market data (spot price and TWAP)
    /// @param betaData Encoded beta market data (spot price only)
    /// @return quote The calculated quote amount
    function quote(
        QuoteParams memory params, 
        bytes memory alphaData, 
        bytes memory betaData
    ) external pure override returns (uint256 quote) {
        // Decode market data
        (uint256 alphaSpotPrice, uint256 alphaTwapPrice) = abi.decode(alphaData, (uint256, uint256));
        uint256 betaSpotPrice = abi.decode(betaData, (uint256));

        // Sophisticated pricing using both data sources
        // Use alpha TWAP as base, adjust with alpha spot for recent trends, and beta spot for cross-validation

        // Calculate price spread between alpha and beta
        uint256 spread = alphaSpotPrice > betaSpotPrice ? 
            alphaSpotPrice - betaSpotPrice : 
            betaSpotPrice - alphaSpotPrice;

        // Use weighted average with spread adjustment
        uint256 basePrice = (alphaTwapPrice * 40 + alphaSpotPrice * 35 + betaSpotPrice * 25) / 100;

        // Apply spread-based adjustment (higher spread = higher uncertainty = worse pricing)
        uint256 spreadPenalty = (spread * 1000) / basePrice; // basis points
        uint256 adjustedPrice = basePrice * (10000 - spreadPenalty) / 10000;

        if (params.zeroForOne) {
            quote = (params.amount * adjustedPrice) / 1e18;
        } else {
            quote = (params.amount * 1e18) / adjustedPrice;
        }

        // Apply premium fee for dual data service (0.5%)
        quote = (quote * 995) / 1000;
    }

    /// @notice Calculate quotes for multiple swaps using both alpha and beta market data
    /// @param params Batch quote parameters
    /// @param alphaData Encoded alpha market data (same for all quotes in batch)
    /// @param betaData Encoded beta market data (same for all quotes in batch)
    /// @return quotes Array of calculated quote amounts
    function quoteBatch(
        QuoteParamsBatch memory params, 
        bytes memory alphaData, 
        bytes memory betaData
    ) external pure override returns (uint256[] memory quotes) {
        // Decode market data once for the batch
        (uint256 alphaSpotPrice, uint256 alphaTwapPrice) = abi.decode(alphaData, (uint256, uint256));
        uint256 betaSpotPrice = abi.decode(betaData, (uint256));

        uint256 spread = alphaSpotPrice > betaSpotPrice ? 
            alphaSpotPrice - betaSpotPrice : 
            betaSpotPrice - alphaSpotPrice;

        uint256 basePrice = (alphaTwapPrice * 40 + alphaSpotPrice * 35 + betaSpotPrice * 25) / 100;
        uint256 spreadPenalty = (spread * 1000) / basePrice;
        uint256 adjustedPrice = basePrice * (10000 - spreadPenalty) / 10000;

        quotes = new uint256[](params.amount.length);

        for (uint256 i = 0; i < params.amount.length; i++) {
            if (params.zeroForOne) {
                quotes[i] = (params.amount[i] * adjustedPrice) / 1e18;
            } else {
                quotes[i] = (params.amount[i] * 1e18) / adjustedPrice;
            }

            // Apply premium fee
            quotes[i] = (quotes[i] * 995) / 1000;
        }
    }
}

contracts/quoters/SimpleQuoter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {IQuoterNoData} from "../interfaces/internal/quoters/IQuoterNoData.sol";
import {QuoteParams, QuoteParamsBatch} from "../structs/QuoteParams.sol";

/// @title SimpleQuoter
/// @notice A quoter that doesn't require external market data - uses only on-chain information
/// @dev This is a skeleton implementation with dummy logic - real pricing logic will be added later
contract SimpleQuoter is IQuoterNoData {
    // execution cost around 10,000 gas
    /// @notice Calculate quote using only on-chain data (balances, amounts)
    /// @param params Quote parameters containing asset balances and swap amount
    /// @return quote The calculated quote amount
    function quote(QuoteParams memory params) external pure override returns (uint256 quote) {
        // Dummy implementation: simple constant product formula approximation
        // In real implementation, this would use sophisticated on-chain pricing logic

        if (params.zeroForOne) {
            // Selling asset0 for asset1
            // Simple formula: quote = (amount * asset1Balance) / (asset0Balance + amount)
            quote = (params.amount * params.asset1Balance) / (params.asset0Balance + params.amount);
        } else {
            // Selling asset1 for asset0  
            // Simple formula: quote = (amount * asset0Balance) / (asset1Balance + amount)
            quote = (params.amount * params.asset0Balance) / (params.asset1Balance + params.amount);
        }

        // Apply a small fee (0.3%)
        quote = (quote * 997) / 1000;
    }

    /// @notice Calculate quotes for multiple swaps using only on-chain data
    /// @param params Batch quote parameters
    /// @return quotes Array of calculated quote amounts
    function quoteBatch(QuoteParamsBatch memory params) external pure override returns (uint256[] memory quotes) {
        quotes = new uint256[](params.amount.length);

        for (uint256 i = 0; i < params.amount.length; i++) {
            if (params.zeroForOne) {
                quotes[i] = (params.amount[i] * params.asset1Balances[i]) / (params.asset0Balances[i] + params.amount[i]);
            } else {
                quotes[i] = (params.amount[i] * params.asset0Balances[i]) / (params.asset1Balances[i] + params.amount[i]);
            }
            // Apply fee
            quotes[i] = (quotes[i] * 997) / 1000;
        }
    }
}

contracts/security/ReentrancyGuard.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @title ReentrancyGuard
/// @notice Reentrancy guard implemented with EIP-1153 transient storage
abstract contract ReentrancyGuard {
    // Base domain separator for guard slots
    bytes32 private constant _REENTRANCY_GUARD_DOMAIN = keccak256("dex.reentrancy.guard:v1");

    modifier nonReentrant() {
        _enterReentrancyGuard(_globalGuardSlot());
        _;
        _exitReentrancyGuard(_globalGuardSlot());
    }

    modifier nonReentrantKey(bytes32 key) {
        bytes32 slot = _keyedGuardSlot(key);
        _enterReentrancyGuard(slot);
        _;
        _exitReentrancyGuard(slot);
    }

    function _globalGuardSlot() internal view returns (bytes32 slot) {
        slot = keccak256(abi.encodePacked(_REENTRANCY_GUARD_DOMAIN, address(this)));
    }

    function _keyedGuardSlot(bytes32 key) internal view returns (bytes32 slot) {
        slot = keccak256(abi.encodePacked(_REENTRANCY_GUARD_DOMAIN, address(this), key));
    }

    function _enterReentrancyGuard(bytes32 slot) private {
        assembly {
            if tload(slot) { revert(0, 0) }
            tstore(slot, 1)
        }
    }

    function _exitReentrancyGuard(bytes32 slot) private {
        assembly {
            tstore(slot, 0)
        }
    }
}

