// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

library TransientStorage {
    /// @notice Store data in transient storage using tstore opcode
    /// @param key Address to use as storage key
    /// @param data Bytes data to store
    function storeTransient(address key, bytes memory data) internal {
        assembly {
            // Use address as the base slot directly for transient storage
            let slot := key
            // Store the data length first
            let dataLength := mload(data)
            tstore(slot, dataLength)
            // Store data in 32-byte chunks sequentially in transient storage
            let dataPtr := add(data, 0x20)
            let chunks := div(add(dataLength, 31), 32)
            for { let i := 0 } lt(i, chunks) { i := add(i, 1) } {
                let chunkSlot := add(slot, add(i, 1))
                let chunkData := mload(add(dataPtr, mul(i, 32)))
                tstore(chunkSlot, chunkData)
            }
        }
    }

    /// @notice Load data from transient storage using tload opcode
    /// @param key Address to use as storage key
    /// @return result Bytes data retrieved from transient storage
    function loadTransient(address key) internal view returns (bytes memory result) {
        assembly {
            let slot := key
            // Load the data length first
            let dataLength := tload(slot)
            // If no data, return empty bytes
            if iszero(dataLength) {
                result := mload(0x40)
                mstore(result, 0)
                mstore(0x40, add(result, 0x20))
            }
            // If data exists, load it
            if gt(dataLength, 0) {
                // Allocate memory for result
                result := mload(0x40)
                mstore(result, dataLength)
                let resultPtr := add(result, 0x20)
                // Load data in 32-byte chunks
                let chunks := div(add(dataLength, 31), 32)
                for { let i := 0 } lt(i, chunks) { i := add(i, 1) } {
                    let chunkSlot := add(slot, add(i, 1))
                    let chunkData := tload(chunkSlot)
                    mstore(add(resultPtr, mul(i, 32)), chunkData)
                }
                // Update free memory pointer
                mstore(0x40, add(result, add(0x20, mul(chunks, 32))))
            }
        }
    }
}

contracts/Core/PoolRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {PoolInfo} from "./structs/PoolInfo.sol";

/**
 * @title PoolRegistry
 * @notice Centralized registry for pool metadata and statistics
 * @dev Separates pool metadata from core pool logic for cleaner architecture
 */
contract PoolRegistry {

    /*//////////////////////////////////////////////////////////////
                                 STRUCTS
    //////////////////////////////////////////////////////////////*/

    struct PoolMetadata {
        address asset0;
        address asset1;
        address quoter;
        bytes3 markings;
        uint256 createdAt;
        uint256 totalVolume;
        uint256 totalFees;
        bool isActive;
        string name;
        string description;
    }

    struct PoolStatistics {
        uint256 swapCount;
        uint256 liquidityEventCount;
        uint256 lastActivityTimestamp;
        uint256 highestLiquidity;
        uint256 lowestLiquidity;
        uint128 avgSwapSize;
    }

    /*//////////////////////////////////////////////////////////////
                                 STATE
    //////////////////////////////////////////////////////////////*/

    // Pool metadata storage
    mapping(uint256 => PoolMetadata) public poolMetadata;
    mapping(uint256 => PoolStatistics) public poolStatistics;

    // Quick lookup mappings
    mapping(bytes32 => uint256) public poolLookup; // keccak256(asset0, asset1, quoter, markings) => poolId
    mapping(address => uint256[]) public poolsByAsset; // asset => poolIds containing this asset
    mapping(address => uint256[]) public poolsByQuoter; // quoter => poolIds using this quoter

    // Pool count for iteration
    uint256 public totalPools;
    uint256[] public allPoolIds;

    // Access control
    address public poolManager;
    mapping(address => bool) public authorized;

    /*//////////////////////////////////////////////////////////////
                                 EVENTS
    //////////////////////////////////////////////////////////////*/

    event PoolRegistered(
        uint256 indexed poolId,
        address indexed asset0,
        address indexed asset1,
        address quoter,
        bytes3 markings,
        uint256 timestamp
    );

    event PoolMetadataUpdated(
        uint256 indexed poolId,
        string name,
        string description
    );

    event PoolDeactivated(uint256 indexed poolId, uint256 timestamp);
    event PoolReactivated(uint256 indexed poolId, uint256 timestamp);

    event PoolStatisticsUpdated(
        uint256 indexed poolId,
        uint256 swapCount,
        uint256 volume,
        uint256 fees
    );

    /*//////////////////////////////////////////////////////////////
                                 MODIFIERS
    //////////////////////////////////////////////////////////////*/

    modifier onlyPoolManager() {
        require(msg.sender == poolManager, "PoolRegistry: Not PoolManager");
        _;
    }

    modifier onlyAuthorized() {
        require(msg.sender == poolManager || authorized[msg.sender], "PoolRegistry: Not authorized");
        _;
    }

    /*//////////////////////////////////////////////////////////////
                                CONSTRUCTOR
    //////////////////////////////////////////////////////////////*/

    constructor(address _poolManager) {
        poolManager = _poolManager;
    }

    /*//////////////////////////////////////////////////////////////
                            REGISTRATION FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Register a new pool in the registry
     * @dev Called by PoolManager when creating a new pool
     */
    function registerPool(
        uint256 poolId,
        address asset0,
        address asset1,
        address quoter,
        bytes3 markings
    ) external onlyPoolManager returns (bool) {
        // Check if pool already exists
        if (poolMetadata[poolId].createdAt != 0) {
            return false; // Pool already registered
        }

        // Store metadata
        poolMetadata[poolId] = PoolMetadata({
            asset0: asset0,
            asset1: asset1,
            quoter: quoter,
            markings: markings,
            createdAt: block.timestamp,
            totalVolume: 0,
            totalFees: 0,
            isActive: true,
            name: "",
            description: ""
        });

        // Initialize statistics
        poolStatistics[poolId] = PoolStatistics({
            swapCount: 0,
            liquidityEventCount: 0,
            lastActivityTimestamp: block.timestamp,
            highestLiquidity: 0,
            lowestLiquidity: type(uint256).max,
            avgSwapSize: 0
        });

        // Update lookup mappings
        bytes32 lookupKey = keccak256(abi.encodePacked(asset0, asset1, quoter, markings));
        poolLookup[lookupKey] = poolId;

        // Add to asset mappings
        poolsByAsset[asset0].push(poolId);
        if (asset1 != asset0) {
            poolsByAsset[asset1].push(poolId);
        }

        // Add to quoter mapping
        poolsByQuoter[quoter].push(poolId);

        // Update global counters
        allPoolIds.push(poolId);
        totalPools++;

        emit PoolRegistered(poolId, asset0, asset1, quoter, markings, block.timestamp);

        return true;
    }

    /*//////////////////////////////////////////////////////////////
                            UPDATE FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Update pool metadata (name, description)
     * @dev Can be called by authorized addresses to add descriptive information
     */
    function updatePoolMetadata(
        uint256 poolId,
        string calldata name,
        string calldata description
    ) external onlyAuthorized {
        require(poolMetadata[poolId].createdAt != 0, "PoolRegistry: Pool not found");

        poolMetadata[poolId].name = name;
        poolMetadata[poolId].description = description;

        emit PoolMetadataUpdated(poolId, name, description);
    }

    /**
     * @notice Record a swap event for statistics
     * @dev Called by PoolManager after successful swap
     */
    function recordSwap(
        uint256 poolId,
        uint256 swapSize,
        uint256 volumeInUSD,
        uint256 feesCollected
    ) external onlyPoolManager {
        PoolStatistics storage stats = poolStatistics[poolId];
        PoolMetadata storage meta = poolMetadata[poolId];

        stats.swapCount++;
        stats.lastActivityTimestamp = block.timestamp;

        // Update average swap size (simple moving average)
        if (stats.avgSwapSize == 0) {
            stats.avgSwapSize = uint128(swapSize);
        } else {
            stats.avgSwapSize = uint128((uint256(stats.avgSwapSize) * 9 + swapSize) / 10);
        }

        // Update totals
        meta.totalVolume += volumeInUSD;
        meta.totalFees += feesCollected;

        emit PoolStatisticsUpdated(poolId, stats.swapCount, meta.totalVolume, meta.totalFees);
    }

    /**
     * @notice Record a liquidity event for statistics
     * @dev Called by PoolManager after add/remove liquidity
     */
    function recordLiquidityEvent(
        uint256 poolId,
        uint256 currentLiquidity
    ) external onlyPoolManager {
        PoolStatistics storage stats = poolStatistics[poolId];

        stats.liquidityEventCount++;
        stats.lastActivityTimestamp = block.timestamp;

        if (currentLiquidity > stats.highestLiquidity) {
            stats.highestLiquidity = currentLiquidity;
        }
        if (currentLiquidity < stats.lowestLiquidity && currentLiquidity > 0) {
            stats.lowestLiquidity = currentLiquidity;
        }
    }

    /*//////////////////////////////////////////////////////////////
                            ADMIN FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Deactivate a pool (for emergency or maintenance)
     */
    function deactivatePool(uint256 poolId) external onlyAuthorized {
        require(poolMetadata[poolId].isActive, "PoolRegistry: Already inactive");
        poolMetadata[poolId].isActive = false;
        emit PoolDeactivated(poolId, block.timestamp);
    }

    /**
     * @notice Reactivate a pool
     */
    function reactivatePool(uint256 poolId) external onlyAuthorized {
        require(!poolMetadata[poolId].isActive, "PoolRegistry: Already active");
        poolMetadata[poolId].isActive = true;
        emit PoolReactivated(poolId, block.timestamp);
    }

    /**
     * @notice Authorize an address for admin functions
     */
    function setAuthorized(address account, bool status) external onlyPoolManager {
        authorized[account] = status;
    }

    /*//////////////////////////////////////////////////////////////
                            VIEW FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Get all pools for a specific asset
     */
    function getPoolsByAsset(address asset) external view returns (uint256[] memory) {
        return poolsByAsset[asset];
    }

    /**
     * @notice Get all pools using a specific quoter
     */
    function getPoolsByQuoter(address quoter) external view returns (uint256[] memory) {
        return poolsByQuoter[quoter];
    }

    /**
     * @notice Get pool by lookup key
     */
    function getPoolByKey(
        address asset0,
        address asset1,
        address quoter,
        bytes3 markings
    ) external view returns (uint256) {
        bytes32 key = keccak256(abi.encodePacked(asset0, asset1, quoter, markings));
        return poolLookup[key];
    }

    /**
     * @notice Check if a pool is active
     */
    function isPoolActive(uint256 poolId) external view returns (bool) {
        return poolMetadata[poolId].isActive;
    }

    /**
     * @notice Get all pool IDs (for iteration)
     */
    function getAllPoolIds() external view returns (uint256[] memory) {
        return allPoolIds;
    }

    /**
     * @notice Get detailed pool information
     */
    function getPoolDetails(uint256 poolId) external view returns (
        PoolMetadata memory metadata,
        PoolStatistics memory statistics
    ) {
        return (poolMetadata[poolId], poolStatistics[poolId]);
    }
}

contracts/Core/security/CircuitBreaker.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/**
 * @title CircuitBreaker
 * @notice Emergency pause mechanism and rate limiting for pools
 * @dev Provides multiple layers of protection against attacks and unusual activity
 */
contract CircuitBreaker {

    /*//////////////////////////////////////////////////////////////
                                 STRUCTS
    //////////////////////////////////////////////////////////////*/

    struct PoolLimits {
        uint256 maxSwapSize;           // Maximum size for a single swap
        uint256 dailyVolumeLimit;      // Maximum volume in 24 hours
        uint256 cooldownPeriod;        // Minimum time between large operations
        uint256 maxPriceImpact;        // Maximum allowed price impact (basis points)
        bool isPaused;                 // Emergency pause flag
        uint256 pausedUntil;          // Timestamp when pause ends (0 = indefinite)
    }

    struct PoolActivity {
        uint256 lastLargeOperation;    // Timestamp of last large operation
        uint256 dailyVolume;          // Rolling 24-hour volume
        uint256 lastVolumeReset;      // Last time daily volume was reset
        uint256 consecutiveFailures;  // Count of consecutive failed operations
        uint256 suspiciousActivity;   // Counter for suspicious patterns
    }

    /*//////////////////////////////////////////////////////////////
                                 STATE
    //////////////////////////////////////////////////////////////*/

    // Pool-specific limits and activity tracking
    mapping(uint256 => PoolLimits) public poolLimits;
    mapping(uint256 => PoolActivity) public poolActivity;

    // Global circuit breaker
    bool public globalPause;
    uint256 public globalPauseUntil;

    // Thresholds for automatic triggers
    uint256 public constant MAX_CONSECUTIVE_FAILURES = 3;
    uint256 public constant SUSPICIOUS_ACTIVITY_THRESHOLD = 5;
    uint256 public constant DEFAULT_COOLDOWN = 1 minutes;
    uint256 public constant VOLUME_RESET_PERIOD = 24 hours;

    // Access control
    address public poolManager;
    mapping(address => bool) public guardians; // Can trigger emergency pause

    /*//////////////////////////////////////////////////////////////
                                 EVENTS
    //////////////////////////////////////////////////////////////*/

    event CircuitBreakerTriggered(
        uint256 indexed poolId,
        string reason,
        uint256 pausedUntil
    );

    event CircuitBreakerReset(uint256 indexed poolId);

    event GlobalPauseActivated(uint256 pausedUntil);
    event GlobalPauseDeactivated();

    event LimitsUpdated(
        uint256 indexed poolId,
        uint256 maxSwapSize,
        uint256 dailyVolumeLimit,
        uint256 cooldownPeriod
    );

    event SuspiciousActivityDetected(
        uint256 indexed poolId,
        string activityType,
        uint256 severity
    );

    /*//////////////////////////////////////////////////////////////
                                 MODIFIERS
    //////////////////////////////////////////////////////////////*/

    modifier onlyPoolManager() {
        require(msg.sender == poolManager, "CircuitBreaker: Not PoolManager");
        _;
    }

    modifier onlyGuardian() {
        require(guardians[msg.sender] || msg.sender == poolManager, "CircuitBreaker: Not guardian");
        _;
    }

    modifier notGloballyPaused() {
        require(!globalPause || block.timestamp > globalPauseUntil, "CircuitBreaker: Globally paused");
        _;
    }

    /*//////////////////////////////////////////////////////////////
                                CONSTRUCTOR
    //////////////////////////////////////////////////////////////*/

    constructor(address _poolManager) {
        poolManager = _poolManager;
        guardians[_poolManager] = true;
    }

    /*//////////////////////////////////////////////////////////////
                            CIRCUIT BREAKER CHECKS
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Check if an operation should be allowed
     * @dev Main entry point for PoolManager to check before operations
     * @param poolId The pool to check
     * @param operationSize Size of the operation (in base units)
     * @param isLargeOperation Whether this is considered a large operation
     * @return allowed Whether the operation should proceed
     * @return reason If not allowed, the reason why
     */
    function checkOperation(
        uint256 poolId,
        uint256 operationSize,
        bool isLargeOperation
    ) external view notGloballyPaused returns (bool allowed, string memory reason) {
        PoolLimits memory limits = poolLimits[poolId];
        PoolActivity memory activity = poolActivity[poolId];

        // Check if pool is paused
        if (limits.isPaused) {
            if (limits.pausedUntil == 0 || block.timestamp < limits.pausedUntil) {
                return (false, "Pool is paused");
            }
        }

        // Check max swap size
        if (limits.maxSwapSize > 0 && operationSize > limits.maxSwapSize) {
            return (false, "Exceeds max swap size");
        }

        // Check cooldown for large operations
        if (isLargeOperation && limits.cooldownPeriod > 0) {
            if (block.timestamp < activity.lastLargeOperation + limits.cooldownPeriod) {
                return (false, "Cooldown period active");
            }
        }

        // Check daily volume limit
        if (limits.dailyVolumeLimit > 0) {
            uint256 currentDailyVolume = _getCurrentDailyVolume(poolId);
            if (currentDailyVolume + operationSize > limits.dailyVolumeLimit) {
                return (false, "Exceeds daily volume limit");
            }
        }

        // Check for suspicious activity
        if (activity.suspiciousActivity >= SUSPICIOUS_ACTIVITY_THRESHOLD) {
            return (false, "Suspicious activity detected");
        }

        // Check consecutive failures
        if (activity.consecutiveFailures >= MAX_CONSECUTIVE_FAILURES) {
            return (false, "Too many consecutive failures");
        }

        return (true, "");
    }

    /**
     * @notice Record a successful operation
     * @dev Called by PoolManager after successful operation
     */
    function recordSuccess(
        uint256 poolId,
        uint256 operationSize,
        bool isLargeOperation
    ) external onlyPoolManager {
        PoolActivity storage activity = poolActivity[poolId];

        // Reset consecutive failures on success
        activity.consecutiveFailures = 0;

        // Update volume tracking
        _updateDailyVolume(poolId, operationSize);

        // Record large operation timestamp
        if (isLargeOperation) {
            activity.lastLargeOperation = block.timestamp;
        }

        // Decay suspicious activity counter on successful operations
        if (activity.suspiciousActivity > 0) {
            activity.suspiciousActivity--;
        }
    }

    /**
     * @notice Record a failed operation
     * @dev Called by PoolManager after failed operation
     */
    function recordFailure(uint256 poolId) external onlyPoolManager {
        PoolActivity storage activity = poolActivity[poolId];
        activity.consecutiveFailures++;

        // Auto-pause if too many failures
        if (activity.consecutiveFailures >= MAX_CONSECUTIVE_FAILURES) {
            _triggerCircuitBreaker(poolId, "Max consecutive failures", 1 hours);
        }
    }

    /**
     * @notice Report suspicious activity
     * @dev Can be called by guardians or automated monitoring
     */
    function reportSuspiciousActivity(
        uint256 poolId,
        string calldata activityType,
        uint256 severity
    ) external onlyGuardian {
        require(severity > 0 && severity <= 10, "Invalid severity");

        PoolActivity storage activity = poolActivity[poolId];
        activity.suspiciousActivity += severity;

        emit SuspiciousActivityDetected(poolId, activityType, severity);

        // Auto-pause if threshold reached
        if (activity.suspiciousActivity >= SUSPICIOUS_ACTIVITY_THRESHOLD) {
            _triggerCircuitBreaker(poolId, "Suspicious activity threshold", 6 hours);
        }
    }

    /*//////////////////////////////////////////////////////////////
                            INTERNAL FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    function _triggerCircuitBreaker(
        uint256 poolId,
        string memory reason,
        uint256 pauseDuration
    ) internal {
        poolLimits[poolId].isPaused = true;
        poolLimits[poolId].pausedUntil = block.timestamp + pauseDuration;

        emit CircuitBreakerTriggered(poolId, reason, poolLimits[poolId].pausedUntil);
    }

    function _getCurrentDailyVolume(uint256 poolId) internal view returns (uint256) {
        PoolActivity memory activity = poolActivity[poolId];

        // Reset if more than 24 hours have passed
        if (block.timestamp > activity.lastVolumeReset + VOLUME_RESET_PERIOD) {
            return 0;
        }

        return activity.dailyVolume;
    }

    function _updateDailyVolume(uint256 poolId, uint256 amount) internal {
        PoolActivity storage activity = poolActivity[poolId];

        // Reset if more than 24 hours have passed
        if (block.timestamp > activity.lastVolumeReset + VOLUME_RESET_PERIOD) {
            activity.dailyVolume = amount;
            activity.lastVolumeReset = block.timestamp;
        } else {
            activity.dailyVolume += amount;
        }
    }

    /*//////////////////////////////////////////////////////////////
                            ADMIN FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Set limits for a pool
     * @dev Should be called when a pool is created or limits need updating
     */
    function setPoolLimits(
        uint256 poolId,
        uint256 maxSwapSize,
        uint256 dailyVolumeLimit,
        uint256 cooldownPeriod,
        uint256 maxPriceImpact
    ) external onlyPoolManager {
        poolLimits[poolId] = PoolLimits({
            maxSwapSize: maxSwapSize,
            dailyVolumeLimit: dailyVolumeLimit,
            cooldownPeriod: cooldownPeriod,
            maxPriceImpact: maxPriceImpact,
            isPaused: false,
            pausedUntil: 0
        });

        emit LimitsUpdated(poolId, maxSwapSize, dailyVolumeLimit, cooldownPeriod);
    }

    /**
     * @notice Emergency pause a specific pool
     */
    function pausePool(uint256 poolId, uint256 duration) external onlyGuardian {
        poolLimits[poolId].isPaused = true;
        poolLimits[poolId].pausedUntil = duration == 0 ? 0 : block.timestamp + duration;

        emit CircuitBreakerTriggered(poolId, "Manual pause", poolLimits[poolId].pausedUntil);
    }

    /**
     * @notice Resume a paused pool
     */
    function resumePool(uint256 poolId) external onlyGuardian {
        poolLimits[poolId].isPaused = false;
        poolLimits[poolId].pausedUntil = 0;
        poolActivity[poolId].consecutiveFailures = 0;
        poolActivity[poolId].suspiciousActivity = 0;

        emit CircuitBreakerReset(poolId);
    }

    /**
     * @notice Activate global pause (emergency only)
     */
    function activateGlobalPause(uint256 duration) external onlyGuardian {
        globalPause = true;
        globalPauseUntil = duration == 0 ? 0 : block.timestamp + duration;

        emit GlobalPauseActivated(globalPauseUntil);
    }

    /**
     * @notice Deactivate global pause
     */
    function deactivateGlobalPause() external onlyGuardian {
        globalPause = false;
        globalPauseUntil = 0;

        emit GlobalPauseDeactivated();
    }

    /**
     * @notice Add or remove a guardian
     */
    function setGuardian(address guardian, bool status) external onlyPoolManager {
        guardians[guardian] = status;
    }

    /*//////////////////////////////////////////////////////////////
                            VIEW FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Check if a pool is currently paused
     */
    function isPoolPaused(uint256 poolId) external view returns (bool) {
        if (globalPause && (globalPauseUntil == 0 || block.timestamp < globalPauseUntil)) {
            return true;
        }

        PoolLimits memory limits = poolLimits[poolId];
        return limits.isPaused && (limits.pausedUntil == 0 || block.timestamp < limits.pausedUntil);
    }

    /**
     * @notice Get remaining cooldown time for a pool
     */
    function getRemainingCooldown(uint256 poolId) external view returns (uint256) {
        PoolActivity memory activity = poolActivity[poolId];
        PoolLimits memory limits = poolLimits[poolId];

        if (block.timestamp >= activity.lastLargeOperation + limits.cooldownPeriod) {
            return 0;
        }

        return (activity.lastLargeOperation + limits.cooldownPeriod) - block.timestamp;
    }
}

contracts/Core/security/MEVProtection.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {MEVProtectionLevel} from "../structs/MEVProtectionLevel.sol";
import {TransientStorage} from "../libraries/TransientStorage.sol";

/**
 * @title MEVProtection
 * @notice Modular MEV protection system - pools choose their protection level
 * @dev Non-opinionated: each pool can balance security vs gas costs
 */
contract MEVProtection {
    using TransientStorage for bytes32;
    using MEVProtectionLevel for uint8;

    /*//////////////////////////////////////////////////////////////
                                 STRUCTS
    //////////////////////////////////////////////////////////////*/

    struct PoolMEVConfig {
        uint8 protectionLevel;      // Bitfield of active protections
        uint256 largeTradeThreshold; // Threshold for enhanced protection
        uint256 baseFee;            // Base fee in basis points
        uint256 surgeFee;           // Additional fee during high activity
        uint256 commitRevealDelay;  // Blocks to wait for reveal
        uint256 gradualFillBlocks;  // Blocks for gradual fill
        uint256 timeLockPeriod;     // Minimum seconds between trades
        address[] whitelist;        // For private pools
    }

    struct Commitment {
        bytes32 commitHash;         // Hash of trade parameters
        uint256 commitBlock;        // Block when committed
        address trader;             // Who committed
        uint256 deadline;           // Reveal deadline
    }

    struct GradualOrder {
        uint256 startPrice;         // Initial price (unfavorable)
        uint256 endPrice;          // Target price (favorable)
        uint256 startBlock;        // When auction started
        uint256 endBlock;          // When auction ends
        uint256 amountRemaining;   // Amount still to fill
    }

    struct ActivityTracker {
        uint256 recentVolume;      // Rolling volume window
        uint256 lastUpdateBlock;   // Last activity update
        uint256 suspicionScore;    // MEV detection score
        mapping(address => uint256) lastTradeTime; // Per-trader cooldowns
    }

    /*//////////////////////////////////////////////////////////////
                                 STATE
    //////////////////////////////////////////////////////////////*/

    // Pool-specific MEV configurations
    mapping(uint256 => PoolMEVConfig) public poolConfigs;

    // Commit-reveal storage
    mapping(uint256 => mapping(address => Commitment)) public commitments;

    // Gradual fill orders
    mapping(uint256 => mapping(uint256 => GradualOrder)) public gradualOrders;
    uint256 public nextOrderId;

    // Activity tracking for dynamic fees
    mapping(uint256 => ActivityTracker) private activityTrackers;

    // Pool manager for access control
    address public immutable poolManager;

    /*//////////////////////////////////////////////////////////////
                                 EVENTS
    //////////////////////////////////////////////////////////////*/

    event MEVConfigSet(
        uint256 indexed poolId,
        uint8 protectionLevel,
        uint256 threshold,
        string description
    );

    event TradeCommitted(
        uint256 indexed poolId,
        address indexed trader,
        bytes32 commitHash,
        uint256 revealDeadline
    );

    event TradeRevealed(
        uint256 indexed poolId,
        address indexed trader,
        uint256 amountIn,
        uint256 amountOut
    );

    event GradualOrderCreated(
        uint256 indexed poolId,
        uint256 indexed orderId,
        uint256 startPrice,
        uint256 endPrice
    );

    event SandwichAttackDetected(
        uint256 indexed poolId,
        address indexed suspectedAttacker,
        uint256 suspicionScore
    );

    event DynamicFeeApplied(
        uint256 indexed poolId,
        uint256 baseFee,
        uint256 surgeFee,
        uint256 totalFee
    );

    /*//////////////////////////////////////////////////////////////
                                 MODIFIERS
    //////////////////////////////////////////////////////////////*/

    modifier onlyPoolManager() {
        require(msg.sender == poolManager, "MEV: Not pool manager");
        _;
    }

    /*//////////////////////////////////////////////////////////////
                                CONSTRUCTOR
    //////////////////////////////////////////////////////////////*/

    constructor(address _poolManager) {
        poolManager = _poolManager;
    }

    /*//////////////////////////////////////////////////////////////
                            CONFIGURATION FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Configure MEV protection for a pool
     * @dev Called during pool creation or by pool admin
     * @param poolId The pool to configure
     * @param level Protection level (bitfield)
     * @param threshold Large trade threshold (0 = always protect)
     */
    function configurePoolProtection(
        uint256 poolId,
        uint8 level,
        uint256 threshold
    ) external onlyPoolManager {
        PoolMEVConfig storage config = poolConfigs[poolId];

        config.protectionLevel = level;
        config.largeTradeThreshold = threshold;

        // Set reasonable defaults based on protection level
        if (level.isEnabled(MEVProtectionLevel.DYNAMIC_FEE)) {
            config.baseFee = 30;  // 0.3% base
            config.surgeFee = 100; // Up to 1% during high activity
        }

        if (level.isEnabled(MEVProtectionLevel.COMMIT_REVEAL)) {
            config.commitRevealDelay = 2; // 2 blocks (~24 seconds)
        }

        if (level.isEnabled(MEVProtectionLevel.GRADUAL_FILL)) {
            config.gradualFillBlocks = 10; // ~2 minutes
        }

        if (level.isEnabled(MEVProtectionLevel.TIME_LOCK)) {
            config.timeLockPeriod = 12; // 12 seconds minimum
        }

        emit MEVConfigSet(poolId, level, threshold, level.describe());
    }

    /**
     * @notice Set custom parameters for fine-tuning
     * @dev For advanced LPs who want specific configurations
     */
    function setCustomParameters(
        uint256 poolId,
        uint256 baseFee,
        uint256 surgeFee,
        uint256 commitDelay,
        uint256 gradualBlocks,
        uint256 timeLock
    ) external onlyPoolManager {
        PoolMEVConfig storage config = poolConfigs[poolId];

        if (baseFee > 0) config.baseFee = baseFee;
        if (surgeFee > 0) config.surgeFee = surgeFee;
        if (commitDelay > 0) config.commitRevealDelay = commitDelay;
        if (gradualBlocks > 0) config.gradualFillBlocks = gradualBlocks;
        if (timeLock > 0) config.timeLockPeriod = timeLock;
    }

    /*//////////////////////////////////////////////////////////////
                        PROTECTION CHECK FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Check if trade should be protected
     * @dev Main entry point for PoolManager
     * @return needsProtection Whether MEV protection should be applied
     * @return protectionType Type of protection to apply
     * @return adjustedFee Fee after MEV adjustments
     */
    function checkTradeProtection(
        uint256 poolId,
        address trader,
        uint256 amountIn,
        bool isLargeTrade
    ) external view returns (
        bool needsProtection,
        uint8 protectionType,
        uint256 adjustedFee
    ) {
        PoolMEVConfig memory config = poolConfigs[poolId];

        // No protection configured
        if (config.protectionLevel == MEVProtectionLevel.NONE) {
            return (false, 0, config.baseFee);
        }

        // Check if protection should apply
        bool shouldProtect = isLargeTrade || 
            (config.largeTradeThreshold > 0 && amountIn >= config.largeTradeThreshold);

        if (!shouldProtect && config.protectionLevel.isEnabled(MEVProtectionLevel.THRESHOLD_ONLY)) {
            return (false, 0, config.baseFee);
        }

        needsProtection = true;
        protectionType = config.protectionLevel;

        // Calculate dynamic fee if enabled
        if (config.protectionLevel.isEnabled(MEVProtectionLevel.DYNAMIC_FEE)) {
            adjustedFee = _calculateDynamicFee(poolId, amountIn);
        } else {
            adjustedFee = config.baseFee;
        }
    }

    /*//////////////////////////////////////////////////////////////
                        COMMIT-REVEAL MECHANISM
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Commit to a trade (phase 1 of commit-reveal)
     * @dev Hides trade parameters until reveal
     */
    function commitTrade(
        uint256 poolId,
        bytes32 commitHash
    ) external returns (uint256 revealDeadline) {
        PoolMEVConfig memory config = poolConfigs[poolId];
        require(
            config.protectionLevel.isEnabled(MEVProtectionLevel.COMMIT_REVEAL),
            "MEV: Commit-reveal not enabled"
        );

        revealDeadline = block.number + config.commitRevealDelay;

        commitments[poolId][msg.sender] = Commitment({
            commitHash: commitHash,
            commitBlock: block.number,
            trader: msg.sender,
            deadline: revealDeadline
        });

        emit TradeCommitted(poolId, msg.sender, commitHash, revealDeadline);
    }

    /**
     * @notice Reveal and execute trade (phase 2)
     * @dev Verifies commitment and executes if valid
     */
    function verifyAndReveal(
        uint256 poolId,
        address trader,
        uint256 amountIn,
        bool zeroForOne,
        uint256 minAmountOut,
        uint256 nonce
    ) external view returns (bool valid) {
        Commitment memory commit = commitments[poolId][trader];

        // Check reveal window
        require(block.number > commit.commitBlock, "MEV: Too early");
        require(block.number <= commit.deadline, "MEV: Too late");

        // Verify hash
        bytes32 expectedHash = keccak256(abi.encodePacked(
            poolId,
            trader,
            amountIn,
            zeroForOne,
            minAmountOut,
            nonce
        ));

        return commit.commitHash == expectedHash;
    }

    /*//////////////////////////////////////////////////////////////
                        GRADUAL FILL MECHANISM
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Create a gradual fill order (Dutch auction style)
     * @dev Price improves over time to find fair value
     */
    function createGradualOrder(
        uint256 poolId,
        uint256 amountIn,
        uint256 startPrice,
        uint256 endPrice
    ) external returns (uint256 orderId) {
        PoolMEVConfig memory config = poolConfigs[poolId];
        require(
            config.protectionLevel.isEnabled(MEVProtectionLevel.GRADUAL_FILL),
            "MEV: Gradual fill not enabled"
        );

        orderId = nextOrderId++;

        gradualOrders[poolId][orderId] = GradualOrder({
            startPrice: startPrice,
            endPrice: endPrice,
            startBlock: block.number,
            endBlock: block.number + config.gradualFillBlocks,
            amountRemaining: amountIn
        });

        emit GradualOrderCreated(poolId, orderId, startPrice, endPrice);
    }

    /**
     * @notice Get current price for gradual order
     * @dev Linear interpolation between start and end price
     */
    function getCurrentGradualPrice(
        uint256 poolId,
        uint256 orderId
    ) external view returns (uint256 currentPrice) {
        GradualOrder memory order = gradualOrders[poolId][orderId];

        if (block.number >= order.endBlock) {
            return order.endPrice;
        }

        uint256 elapsed = block.number - order.startBlock;
        uint256 duration = order.endBlock - order.startBlock;

        if (order.startPrice > order.endPrice) {
            // Price decreasing (sell order)
            uint256 priceDrop = order.startPrice - order.endPrice;
            uint256 currentDrop = (priceDrop * elapsed) / duration;
            return order.startPrice - currentDrop;
        } else {
            // Price increasing (buy order)
            uint256 priceRise = order.endPrice - order.startPrice;
            uint256 currentRise = (priceRise * elapsed) / duration;
            return order.startPrice + currentRise;
        }
    }

    /*//////////////////////////////////////////////////////////////
                        DYNAMIC FEE CALCULATION
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Calculate dynamic fee based on recent activity
     * @dev Higher fees during suspected sandwich attacks
     */
    function _calculateDynamicFee(
        uint256 poolId,
        uint256 amountIn
    ) private view returns (uint256) {
        PoolMEVConfig memory config = poolConfigs[poolId];
        ActivityTracker storage tracker = activityTrackers[poolId];

        // Base fee always applies
        uint256 fee = config.baseFee;

        // Add surge fee based on recent activity
        uint256 blocksSinceUpdate = block.number - tracker.lastUpdateBlock;
        if (blocksSinceUpdate < 3) {
            // Very recent activity - possible sandwich
            fee += config.surgeFee;

            // Extra fee for suspected sandwich attack
            if (tracker.suspicionScore > 5) {
                fee += config.surgeFee / 2;
            }
        } else if (blocksSinceUpdate < 10) {
            // Recent activity - moderate surge
            fee += config.surgeFee / 2;
        }

        // Volume-based surge
        if (tracker.recentVolume > 0) {
            uint256 volumeRatio = (amountIn * 100) / tracker.recentVolume;
            if (volumeRatio > 10) {
                // Large trade relative to recent volume
                fee += config.surgeFee / 4;
            }
        }

        return fee;
    }

    /*//////////////////////////////////////////////////////////////
                        SANDWICH DETECTION
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Detect potential sandwich attacks
     * @dev Analyzes patterns to identify MEV bots
     */
    function detectSandwichPattern(
        uint256 poolId,
        address trader,
        uint256 amountIn,
        bool isBuy
    ) external onlyPoolManager returns (bool suspected) {
        ActivityTracker storage tracker = activityTrackers[poolId];

        // Check for suspicious patterns
        uint256 timeSinceLastTrade = block.timestamp - tracker.lastTradeTime[trader];

        // Same trader, opposite direction, very quickly
        if (timeSinceLastTrade < 30) {
            tracker.suspicionScore += 3;
        }

        // Large trade immediately after small trade
        if (block.number == tracker.lastUpdateBlock && amountIn > tracker.recentVolume * 2) {
            tracker.suspicionScore += 2;
        }

        // Update tracker
        tracker.recentVolume = (tracker.recentVolume * 9 + amountIn) / 10; // EMA
        tracker.lastUpdateBlock = block.number;
        tracker.lastTradeTime[trader] = block.timestamp;

        // Decay suspicion over time
        if (tracker.suspicionScore > 0 && block.number > tracker.lastUpdateBlock + 10) {
            tracker.suspicionScore--;
        }

        suspected = tracker.suspicionScore > 5;

        if (suspected) {
            emit SandwichAttackDetected(poolId, trader, tracker.suspicionScore);
        }

        return suspected;
    }

    /*//////////////////////////////////////////////////////////////
                        TIME LOCK ENFORCEMENT
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Check if trader can trade (time lock)
     * @dev Prevents rapid-fire trades
     */
    function canTrade(
        uint256 poolId,
        address trader
    ) external view returns (bool allowed, uint256 timeRemaining) {
        PoolMEVConfig memory config = poolConfigs[poolId];

        if (!config.protectionLevel.isEnabled(MEVProtectionLevel.TIME_LOCK)) {
            return (true, 0);
        }

        uint256 lastTrade = activityTrackers[poolId].lastTradeTime[trader];
        if (lastTrade == 0) {
            return (true, 0);
        }

        uint256 elapsed = block.timestamp - lastTrade;
        if (elapsed >= config.timeLockPeriod) {
            return (true, 0);
        }

        return (false, config.timeLockPeriod - elapsed);
    }

    /*//////////////////////////////////////////////////////////////
                        PRIVATE POOL MANAGEMENT
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Manage whitelist for private pools
     * @dev Only whitelisted addresses can trade
     */
    function updateWhitelist(
        uint256 poolId,
        address[] calldata addresses,
        bool add
    ) external onlyPoolManager {
        PoolMEVConfig storage config = poolConfigs[poolId];
        require(
            config.protectionLevel.isEnabled(MEVProtectionLevel.PRIVATE_POOL),
            "MEV: Not a private pool"
        );

        if (add) {
            for (uint256 i = 0; i < addresses.length; i++) {
                config.whitelist.push(addresses[i]);
            }
        } else {
            // Remove addresses (simplified - production would optimize)
            delete config.whitelist;
        }
    }

    /**
     * @notice Check if address is whitelisted
     */
    function isWhitelisted(
        uint256 poolId,
        address trader
    ) external view returns (bool) {
        PoolMEVConfig memory config = poolConfigs[poolId];

        if (!config.protectionLevel.isEnabled(MEVProtectionLevel.PRIVATE_POOL)) {
            return true; // Not a private pool
        }

        for (uint256 i = 0; i < config.whitelist.length; i++) {
            if (config.whitelist[i] == trader) {
                return true;
            }
        }

        return false;
    }

    /*//////////////////////////////////////////////////////////////
                            VIEW FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /**
     * @notice Get complete MEV configuration for a pool
     */
    function getPoolMEVConfig(uint256 poolId) external view returns (
        uint8 protectionLevel,
        string memory description,
        uint256 estimatedGasOverhead,
        uint256 currentFee
    ) {
        PoolMEVConfig memory config = poolConfigs[poolId];
        protectionLevel = config.protectionLevel;
        description = config.protectionLevel.describe();
        estimatedGasOverhead = config.protectionLevel.estimateGasOverhead();
        currentFee = _calculateDynamicFee(poolId, 0);
    }

    /**
     * @notice Check if pool has any MEV protection
     */
    function hasProtection(uint256 poolId) external view returns (bool) {
        return poolConfigs[poolId].protectionLevel != MEVProtectionLevel.NONE;
    }
}

contracts/Core/structs/ExtendedMarkings..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {Markings} from "./Markings.sol";
import {MarkingHelper} from "../libraries/MarkingHelper.sol";
import {MEVProtectionLevel} from "./MEVProtectionLevel.sol";

/**
 * @title ExtendedMarkings
 * @notice Extended pool configuration including MEV protection preferences
 * @dev Maintains backward compatibility while adding new features
 */
library ExtendedMarkings {

    // Extended marking structure (4 bytes total)
    // Byte 0: Original markings (data sources, bucket)
    // Byte 1: MEV protection level
    // Byte 2: Fee tier (0-255 basis points)
    // Byte 3: Reserved for future use

    struct ExtendedPoolConfig {
        // Original markings
        bool data0;
        bool data1;
        bool data2;
        bool data3;
        uint16 bucketID;

        // MEV protection
        uint8 mevProtection;

        // Fee configuration
        uint8 feeTier;        // Base fee in basis points

        // Future expansion
        uint8 reserved;
    }

    /**
     * @notice Encode extended configuration into bytes4
     * @dev Backward compatible - original markings in first 3 bytes
     */
    function encode(ExtendedPoolConfig memory config) internal pure returns (bytes4) {
        // Encode original markings manually (matching MarkingHelper layout)
        // Layout: [bucketID (12 bits)] [data3 (1)] [data2 (1)] [data1 (1)] [data0 (1)]
        uint24 data = 0;
        if (config.data0) data |= 0x1;
        if (config.data1) data |= 0x2;
        if (config.data2) data |= 0x4;
        if (config.data3) data |= 0x8;
        data |= uint24(config.bucketID) << 4;

        bytes3 originalMarkings = bytes3(data);

        // Combine with MEV byte
        bytes4 extended = bytes4(uint32(uint24(originalMarkings)) << 8 | uint32(config.mevProtection));

        return extended;
    }

    /**
     * @notice Decode bytes4 into extended configuration
     */
    function decode(bytes4 extended) internal pure returns (ExtendedPoolConfig memory config) {
        // Extract original markings (first 3 bytes)
        bytes3 originalMarkings = bytes3(extended);
        Markings memory basic = MarkingHelper.decodeMarkings(originalMarkings);

        config.data0 = basic.data0;
        config.data1 = basic.data1;
        config.data2 = basic.data2;
        config.data3 = basic.data3;
        config.bucketID = basic.bucketID;

        // Extract MEV protection (4th byte)
        config.mevProtection = uint8(extended[3]);

        return config;
    }

    /**
     * @notice Create configuration for different pool types
     * @dev Convenience functions for common configurations
     */
    function createBasicAMM(uint16 bucketID) internal pure returns (bytes4) {
        return encode(ExtendedPoolConfig({
            data0: false,
            data1: false,
            data2: false,
            data3: false,
            bucketID: bucketID,
            mevProtection: MEVProtectionLevel.NONE,
            feeTier: 30,  // 0.3% default
            reserved: 0
        }));
    }

    function createOraclePool(
        bool useChainlink,
        bool useRedstone,
        uint16 bucketID,
        uint8 mevLevel
    ) internal pure returns (bytes4) {
        return encode(ExtendedPoolConfig({
            data0: useChainlink,
            data1: useRedstone,
            data2: false,
            data3: false,
            bucketID: bucketID,
            mevProtection: mevLevel,
            feeTier: 10,  // 0.1% for oracle pools
            reserved: 0
        }));
    }

    function createSecurePool(
        bool data0,
        bool data1,
        uint16 bucketID
    ) internal pure returns (bytes4) {
        return encode(ExtendedPoolConfig({
            data0: data0,
            data1: data1,
            data2: false,
            data3: false,
            bucketID: bucketID,
            mevProtection: MEVProtectionLevel.HIGH_PROTECTION,
            feeTier: 50,  // 0.5% for secure pools
            reserved: 0
        }));
    }

    function createPrivatePool(
        uint16 bucketID
    ) internal pure returns (bytes4) {
        return encode(ExtendedPoolConfig({
            data0: false,
            data1: false,
            data2: false,
            data3: false,
            bucketID: bucketID,
            mevProtection: MEVProtectionLevel.PRIVATE_POOL,
            feeTier: 5,   // 0.05% for private pools
            reserved: 0
        }));
    }

    /**
     * @notice Check if pool requires MEV protection
     */
    function requiresMEVProtection(bytes4 extended) internal pure returns (bool) {
        uint8 mevLevel = uint8(extended[3]);
        return mevLevel != MEVProtectionLevel.NONE;
    }

    /**
     * @notice Get human-readable pool type
     */
    function getPoolType(bytes4 extended) internal pure returns (string memory) {
        ExtendedPoolConfig memory config = decode(extended);

        if (config.mevProtection == MEVProtectionLevel.PRIVATE_POOL) {
            return "Private Pool";
        }

        if (config.mevProtection >= MEVProtectionLevel.HIGH_PROTECTION) {
            return "Secure Pool";
        }

        bool hasOracles = config.data0 || config.data1 || config.data2 || config.data3;
        if (hasOracles) {
            return config.mevProtection > 0 ? "Protected Oracle Pool" : "Oracle Pool";
        }

        return config.mevProtection > 0 ? "Protected AMM" : "Basic AMM";
    }

    /**
     * @notice Estimate total gas cost including MEV protection
     */
    function estimateTotalGas(bytes4 extended) internal pure returns (uint256) {
        ExtendedPoolConfig memory config = decode(extended);

        uint256 baseGas = 100000; // Base swap gas

        // Add data source costs
        if (config.data0) baseGas += 30000;
        if (config.data1) baseGas += 30000;
        if (config.data2) baseGas += 30000;
        if (config.data3) baseGas += 30000;

        // Add MEV protection overhead
        baseGas += MEVProtectionLevel.estimateGasOverhead(config.mevProtection);

        return baseGas;
    }
}

