// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;
import {Marking} from "../structs/Marking.sol";

library MarkingHelper {

    // New layout (24 bits total):
    // LSB â†’ MSB: isDefault(0), isMarket(1), marketPtr(2..11) [10 bits], bucketID(12..23) [12 bits]
    function decodeMarkings(bytes3 markings) internal pure returns (Marking memory result) {
        uint24 data = uint24(markings);
        result.isDefault = (data & 0x1) != 0;
        result.isMarket = ((data >> 1) & 0x1) != 0;
        result.marketPtr = uint16((data >> 2) & 0x3FF); // 10 bits
        result.bucketID = uint16((data >> 12) & 0xFFF); // 12 bits
        return result;
    }
}

contracts/Core/libraries/PoolIDAssembly..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

library PoolIDAssembly {
    /// @notice Creates a pool ID as a uint256 for use with ERC6909.
    /// @dev Must match off-chain calculation used in tests: keccak256(abi.encodePacked(asset0, asset1, quoter, markings)).
    function assemblePoolID(
        address asset0,
        address asset1,
        address quoter,
        bytes3 markings
    ) internal pure returns (uint256 poolID) {
        // Canonicalize asset order: asset0 < asset1
        (address a0, address a1) = asset0 < asset1 ? (asset0, asset1) : (asset1, asset0);
        poolID = uint256(keccak256(abi.encodePacked(a0, a1, quoter, markings)));
    }
}

