// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

// Custom errors for gas-efficient reverts
error PoolManager__InsufficientLiquidityMinted();
error PoolManager__InsufficientAsset0(uint256 required, uint256 available);
error PoolManager__InsufficientAsset1(uint256 required, uint256 available);
error PoolManager__PoolAlreadyExists(uint256 poolID);

import {PoolIDAssembly} from "./PoolIDAssembly.sol";
import {AssetTransferLib} from "./AssetTransferLib.sol";
import {FlashAccounting} from "./FlashAccounting.sol";
import {SwapParams} from "../structs/SwapParams.sol";
import {IQuoteRouter} from "../interfaces/internal/IQuoteRouter.sol";
import {Hop} from "../structs/Hop.sol";
import {PoolInfo} from "../structs/PoolInfo.sol";

// (interface moved to contracts/interfaces/internal/IQuoteRouter.sol)

/// @notice Library containing core calculation logic for PoolManager
/// @dev Pure calculation functions and simple storage management
library PoolManagerLib {

    /*//////////////////////////////////////////////////////////////
                                 STRUCTS
    //////////////////////////////////////////////////////////////*/

    // PoolInfo and Hop moved to structs folder

    /*//////////////////////////////////////////////////////////////
                                 STORAGE
    //////////////////////////////////////////////////////////////*/

    struct PoolManagerStorage {
        // Total liquidity (shares) issued per pool
        mapping(uint256 => uint256) totalLiquidity;
        // Pool asset balances (poolID -> packed uint256) - BOTH ASSETS IN SINGLE SLOT!
        // Lower 128 bits = asset0, Upper 128 bits = asset1
        mapping(uint256 => uint256) poolInventories;
        // Pool information for external queries (poolID -> PoolInfo)
        mapping(uint256 => PoolInfo) poolInfos;
    }

    /*//////////////////////////////////////////////////////////////
                                 EVENTS
    //////////////////////////////////////////////////////////////*/

    event PoolCreated(uint256 indexed poolID, address asset0, address asset1, address quoter, bytes3 markings);

    /*//////////////////////////////////////////////////////////////
                            POOL CREATION
    //////////////////////////////////////////////////////////////*/

    /// @notice Creates a new pool with canonical asset ordering (asset0 < asset1)
    /// @param self Storage reference
    /// @param asset0 First asset address
    /// @param asset1 Second asset address  
    /// @param quoter Quoter contract address
    /// @param markings Pool configuration markings
    /// @return poolID The unique identifier for the created pool
    function createPool(
        PoolManagerStorage storage self,
        address asset0,
        address asset1,
        address quoter,
        bytes3 markings
    ) internal returns (uint256 poolID) {
        // PoolIDAssembly will canonicalize the order internally
        poolID = PoolIDAssembly.assemblePoolID(asset0, asset1, quoter, markings);

        // Check if pool already exists by checking if quoter is non-zero
        if (self.poolInfos[poolID].quoter != address(0)) {
            revert PoolManager__PoolAlreadyExists(poolID);
        }

        // Store pool information with canonical asset order
        (address sortedAsset0, address sortedAsset1) = asset0 < asset1 ? (asset0, asset1) : (asset1, asset0);
        self.poolInfos[poolID] = PoolInfo({
            asset0: sortedAsset0,
            asset1: sortedAsset1,
            quoter: quoter,
            markings: markings
        });

        // Emit with canonical order for consistency
        emit PoolCreated(poolID, sortedAsset0, sortedAsset1, quoter, markings);
    }

    /// @notice Get pool information by poolID
    /// @param self Storage reference
    /// @param poolID Pool identifier
    /// @return poolInfo Pool information structure
    function getPoolInfo(
        PoolManagerStorage storage self,
        uint256 poolID
    ) internal view returns (PoolInfo memory poolInfo) {
        return self.poolInfos[poolID];
    }

    /*//////////////////////////////////////////////////////////////
                            LIQUIDITY CALCULATIONS
    //////////////////////////////////////////////////////////////*/

    /// @notice Calculates liquidity to mint for new providers
    /// @param self Storage reference
    /// @param amount0 Amount of asset0 being added
    /// @param amount1 Amount of asset1 being added
    /// @param poolAsset0 Current pool balance of asset0
    /// @param poolAsset1 Current pool balance of asset1
    /// @param poolID Pool identifier for totalLiquidity access
    /// @param rate Exchange rate (asset0/asset1) 1e18 fixed point
    /// @return liquidity Amount of liquidity shares to mint
    function calculateLiquidityToMint(
        PoolManagerStorage storage self,
        uint256 amount0,
        uint256 amount1,
        uint128 poolAsset0,
        uint128 poolAsset1,
        uint256 poolID,
        uint256 rate
    ) internal view returns (uint256 liquidity) {
        // For first liquidity provision, use simple geometric mean
        if (self.totalLiquidity[poolID] == 0) {
            // Simple approach: use the sum of both amounts as initial liquidity
            liquidity = amount0 + amount1;
        } else {
            // Convert amount1 to amount0 equivalent using rate: amount1 * 1e18 / rate
            uint256 valueAdded = amount0 + (amount1 * 1e18) / rate;
            uint256 poolValue = poolAsset0 + (poolAsset1 * 1e18) / rate;

            // Proportional to pool
            liquidity = (valueAdded * self.totalLiquidity[poolID]) / poolValue;
        }

        if (liquidity == 0) revert PoolManager__InsufficientLiquidityMinted();
    }

    /*//////////////////////////////////////////////////////////////
                            STORAGE ACCESS FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /// @notice Get current pool inventory - SINGLE STORAGE READ!
    /// @param self Storage reference
    /// @param poolId Pool identifier
    /// @return asset0 Asset0 balance
    /// @return asset1 Asset1 balance
    function getInventory(
        PoolManagerStorage storage self,
        uint256 poolId
    ) internal view returns (uint128 asset0, uint128 asset1) {
        uint256 packed = self.poolInventories[poolId]; // Single SLOAD!
        asset0 = uint128(packed);              // Lower 128 bits
        asset1 = uint128(packed >> 128);       // Upper 128 bits
    }

    /// @notice Update pool inventory - SINGLE STORAGE OPERATION!
    /// @param self Storage reference
    /// @param poolId Pool identifier
    /// @param asset0Delta Change for asset0 (positive = add, negative = subtract)
    /// @param asset1Delta Change for asset1 (positive = add, negative = subtract)
    function updateInventory(
        PoolManagerStorage storage self,
        uint256 poolId,
        int128 asset0Delta,
        int128 asset1Delta
    ) internal {
        uint256 packed = self.poolInventories[poolId]; // Single SLOAD
        uint128 asset0 = uint128(packed);         // Lower 128 bits
        uint128 asset1 = uint128(packed >> 128);  // Upper 128 bits

        // Apply delta changes
        asset0 = uint128(int128(asset0) + asset0Delta);
        asset1 = uint128(int128(asset1) + asset1Delta);

        // Pack and store only if changed - saves gas on no-op updates
        uint256 newPacked = uint256(asset0) | (uint256(asset1) << 128);
        if (newPacked != packed) {
            self.poolInventories[poolId] = newPacked; // Single SSTORE when needed
        }
    }

    /*//////////////////////////////////////////////////////////////
                            SWAP VALIDATION
    //////////////////////////////////////////////////////////////*/

    /// @notice Validates if a swap can be executed with given inventory
    /// @param poolAsset0 Current asset0 balance
    /// @param poolAsset1 Current asset1 balance
    /// @param amountOut Expected output amount
    /// @param zeroForOne Direction of swap (true = asset0 -> asset1, false = asset1 -> asset0)
    function validateSwapInventory(
        uint128 poolAsset0,
        uint128 poolAsset1,
        uint256 amountOut,
        bool zeroForOne
    ) internal pure {
        // Check if pool has enough of the output asset
        if (zeroForOne) {
            // Swapping asset0 for asset1 - need enough asset1
            if (poolAsset1 < amountOut) revert PoolManager__InsufficientAsset1(amountOut, poolAsset1);
        } else {
            // Swapping asset1 for asset0 - need enough asset0
            if (poolAsset0 < amountOut) revert PoolManager__InsufficientAsset0(amountOut, poolAsset0);
        }
    }

    /*//////////////////////////////////////////////////////////////
                            ASSET TRANSFERS
    //////////////////////////////////////////////////////////////*/

    /// @notice Handles asset transfers for liquidity operations
    /// @param asset0 Asset0 address
    /// @param asset1 Asset1 address
    /// @param amount0 Amount of asset0 to transfer
    /// @param amount1 Amount of asset1 to transfer
    /// @param msgValue ETH value sent with transaction
    /// @param transferIn True for transferIn, false for transferOut
    /// @param recipient Recipient address (for transferOut)
    function handleAssetTransfers(
        address asset0,
        address asset1,
        uint256 amount0,
        uint256 amount1,
        uint256 msgValue,
        bool transferIn,
        address recipient
    ) internal {
        if (transferIn) {
            // Transfer assets in
            if (amount0 > 0) {
                AssetTransferLib.transferIn(asset0, recipient, amount0, msgValue);
            }
            if (amount1 > 0) {
                AssetTransferLib.transferIn(asset1, recipient, amount1, 0); // No ETH for asset1
            }
        } else {
            // Transfer assets out
            if (amount0 > 0) {
                AssetTransferLib.transferOut(asset0, recipient, amount0);
            }
            if (amount1 > 0) {
                AssetTransferLib.transferOut(asset1, recipient, amount1);
            }
        }
    }

    /*//////////////////////////////////////////////////////////////
                            BATCH SWAP PROCESSING
    //////////////////////////////////////////////////////////////*/

    /// @notice Execute batch swap with automatic quoter selection
    /// @param self Storage reference
    /// @param hops Array of hops to execute
    /// @param amountIn Initial input amount
    /// @param msgValue ETH value for first hop
    /// @param recipient Recipient address
    /// @param quoterRouter QuoterRouter contract for quote calls
    /// @return amountOut Final output amount
    function executeBatchSwap(
        PoolManagerStorage storage self,
        Hop[] calldata hops,
        uint256 amountIn,
        uint256 msgValue,
        address recipient,
        address quoterRouter
    ) internal returns (uint256 amountOut) {
        uint256 numHops = hops.length;
        require(numHops > 0, "No hops");

        uint256 intermediateAmount = amountIn;

        // Track user-facing input and output assets for settlement
        address inputAsset;
        address outputAsset;

        for (uint256 i = 0; i < numHops; ) {
            Hop calldata hop = hops[i];
            bool isFirstHop = i == 0;
            bool isLastHop = i == numHops - 1;
            if (isFirstHop) {
                inputAsset = hop.zeroForOne ? hop.asset0 : hop.asset1;
            }
            if (isLastHop) {
                outputAsset = hop.zeroForOne ? hop.asset1 : hop.asset0;
            }

            intermediateAmount = executeHop(
                self,
                hop,
                intermediateAmount,
                isFirstHop ? msgValue : 0,
                isFirstHop,
                isLastHop,
                recipient,
                quoterRouter
            );
            unchecked { ++i; }
        }

        // Perform settlement only if a session is not active; otherwise defer to final session settle
        if (!FlashAccounting.isSessionActive(recipient)) {
            _settleUserDeltas(recipient, inputAsset, outputAsset, amountIn, intermediateAmount, msgValue);
        }

        return intermediateAmount;
    }

    /// @notice Execute a single hop (individual or batch based on array length)
    /// @param self Storage reference
    /// @param hop Hop to execute
    /// @param inputAmount Input amount for this hop
    /// @param msgValue ETH value for first hop
    /// @param isFirstHop Whether this is the first hop
    /// @param isLastHop Whether this is the last hop
    /// @param recipient Recipient address
    /// @param quoterRouter QuoterRouter contract for quote calls
    /// @return outputAmount Output amount from this hop
    function executeHop(
        PoolManagerStorage storage self,
        Hop calldata hop,
        uint256 inputAmount,
        uint256 msgValue,
        bool isFirstHop,
        bool isLastHop,
        address recipient,
        address quoterRouter
    ) internal returns (uint256 outputAmount) {
        require(hop.markings.length > 0, "No markings");
        require(hop.amounts.length == hop.markings.length, "Mismatched arrays");

        if (hop.markings.length == 1) {
            // Single hop - use individual quote
            return executeSingleHop(
                self,
                hop.asset0,
                hop.asset1,
                hop.quoter,
                hop.markings[0],
                hop.zeroForOne,
                inputAmount,
                msgValue,
                isFirstHop,
                isLastHop,
                recipient,
                quoterRouter
            );
        } else {
            // Multiple hops with same quoter - use batch quote
            return executeBatchHopsWithQuoter(
                self,
                hop,
                inputAmount,
                isFirstHop,
                isLastHop,
                recipient,
                quoterRouter
            );
        }
    }

    /// @notice Execute multiple hops with the same quoter using batch quote
    /// @param self Storage reference
    /// @param hop Batch hop with multiple markings/amounts
    /// @param inputAmount Input amount for this batch
    /// @param isFirstHop Whether this is the first hop
    /// @param isLastHop Whether this is the last hop
    /// @param recipient Recipient address
    /// @param quoterRouter QuoterRouter contract for quote calls
    /// @return outputAmount Output amount from this batch
    function executeBatchHopsWithQuoter(
        PoolManagerStorage storage self,
        Hop calldata hop,
        uint256 inputAmount,
        bool isFirstHop,
        bool isLastHop,
        address recipient,
        address quoterRouter
    ) internal returns (uint256 outputAmount) {
        uint256 batchSize = hop.markings.length;

        // Flash accounting: record user owes input for first hop, defer transfer to settlement
        if (isFirstHop) {
            address inAsset = hop.zeroForOne ? hop.asset0 : hop.asset1;
            FlashAccounting.addDelta(recipient, inAsset, -int256(inputAmount));
        }

        // Prepare batch parameters for quoter
        uint128[] memory asset0Balances = new uint128[](batchSize);
        uint128[] memory asset1Balances = new uint128[](batchSize);

        // Get pool balances for all hops in the batch
        for (uint256 i = 0; i < batchSize; ) {
            uint256 poolID = PoolIDAssembly.assemblePoolID(hop.asset0, hop.asset1, hop.quoter, hop.markings[i]);
            (asset0Balances[i], asset1Balances[i]) = getInventory(self, poolID);
            unchecked { ++i; }
        }

        // Create SwapParams for batch quote
        SwapParams memory swapParams = SwapParams({
            asset0: hop.asset0,
            asset1: hop.asset1,
            quoter: hop.quoter,
            amount: hop.amounts,
            zeroForOne: hop.zeroForOne,
            marking: hop.markings
        });

        // Get batch quotes using QuoteRouter
        (uint256[] memory quotes, uint256[] memory poolIDs) = IQuoteRouter(quoterRouter).getQuoteBatch(swapParams, asset0Balances, asset1Balances);

        // Execute all hops in the batch
        for (uint256 i = 0; i < batchSize; ) {
            uint256 hopAmountIn = hop.amounts[i];
            uint256 hopAmountOut = quotes[i];

            // Validate swap inventory
            validateSwapInventory(
                asset0Balances[i], 
                asset1Balances[i], 
                hopAmountOut, 
                hop.zeroForOne
            );

            // Update inventory
            updateInventory(
                self,
                poolIDs[i],
                hop.zeroForOne ? int128(uint128(hopAmountIn)) : -int128(uint128(hopAmountOut)),
                hop.zeroForOne ? -int128(uint128(hopAmountOut)) : int128(uint128(hopAmountIn))
            );
            unchecked { ++i; }
        }

        // Flash accounting: record user is owed output for last hop, defer transfer to settlement
        if (isLastHop) {
            address outAsset = hop.zeroForOne ? hop.asset1 : hop.asset0;
            FlashAccounting.addDelta(recipient, outAsset, int256(quotes[batchSize - 1]));
        }

        return quotes[batchSize - 1];
    }

    /// @notice Execute a single hop
    /// @param self Storage reference
    /// @param asset0 First asset address
    /// @param asset1 Second asset address
    /// @param quoter Quoter contract address
    /// @param marking Pool marking
    /// @param zeroForOne Swap direction
    /// @param inputAmount Input amount
    /// @param msgValue ETH value
    /// @param isFirstHop Whether this is the first hop
    /// @param isLastHop Whether this is the last hop
    /// @param recipient Recipient address
    /// @param quoterRouter QuoterRouter contract for quote calls
    /// @return outputAmount Output amount
    function executeSingleHop(
        PoolManagerStorage storage self,
        address asset0,
        address asset1,
        address quoter,
        bytes3 marking,
        bool zeroForOne,
        uint256 inputAmount,
        uint256 msgValue,
        bool isFirstHop,
        bool isLastHop,
        address recipient,
        address quoterRouter
    ) internal returns (uint256 outputAmount) {
        uint256 poolID = PoolIDAssembly.assemblePoolID(asset0, asset1, quoter, marking);

        // Flash accounting: record user owes input for first hop, defer transfer to settlement
        if (isFirstHop) {
            address inAsset = zeroForOne ? asset0 : asset1;
            FlashAccounting.addDelta(recipient, inAsset, -int256(inputAmount));
        }

        (uint128 poolAsset0, uint128 poolAsset1) = getInventory(self, poolID);

        outputAmount = getHopQuote(
            self,
            asset0,
            asset1,
            quoter,
            marking,
            zeroForOne,
            inputAmount,
            poolAsset0,
            poolAsset1,
            quoterRouter
        );

        validateSwapInventory(poolAsset0, poolAsset1, outputAmount, zeroForOne);

        updateInventory(
            self,
            poolID,
            zeroForOne ? int128(uint128(inputAmount)) : -int128(uint128(outputAmount)),
            zeroForOne ? -int128(uint128(outputAmount)) : int128(uint128(inputAmount))
        );

        if (isLastHop) {
            // Flash accounting: record user is owed output for last hop, defer transfer to settlement
            address outAsset = zeroForOne ? asset1 : asset0;
            FlashAccounting.addDelta(recipient, outAsset, int256(outputAmount));
        }
        // For intermediate hops, the output asset stays in the contract for the next hop
    }

    /// @notice Get quote for a single hop
    /// @param self_ Storage reference (unused)
    /// @param asset0 First asset address
    /// @param asset1 Second asset address
    /// @param quoter Quoter contract address
    /// @param marking Pool marking
    /// @param zeroForOne Swap direction
    /// @param amountIn Input amount
    /// @param poolAsset0 Pool asset0 balance
    /// @param poolAsset1 Pool asset1 balance
    /// @param quoterRouter QuoterRouter contract for quote calls
    /// @return amountOut Output amount
    function getHopQuote(
        PoolManagerStorage storage self_,
        address asset0,
        address asset1,
        address quoter,
        bytes3 marking,
        bool zeroForOne,
        uint256 amountIn,
        uint128 poolAsset0,
        uint128 poolAsset1,
        address quoterRouter
    ) internal returns (uint256 amountOut) {
        uint256[] memory amounts = new uint256[](1);
        amounts[0] = amountIn;
        bytes3[] memory marks = new bytes3[](1);
        marks[0] = marking;
        SwapParams memory p = SwapParams({
            asset0: asset0,
            asset1: asset1,
            quoter: quoter,
            amount: amounts,
            zeroForOne: zeroForOne,
            marking: marks
        });
        (uint256 quote, ) = IQuoteRouter(quoterRouter).getQuote(p, poolAsset0, poolAsset1);
        amountOut = quote > 0
            ? quote
            : (zeroForOne ? (amountIn * 1300000000000000000) / 1e18 : (amountIn * 1e18) / 1300000000000000000);
    }

    /*//////////////////////////////////////////////////////////////
                            FLASH SETTLEMENT
    //////////////////////////////////////////////////////////////*/

    /// @notice Settle user's net deltas for two tokens (input/output) with minimal transfers
    function _settleUserDeltas(
        address user,
        address inputAsset,
        address outputAsset,
        uint256 /*amountIn*/,
        uint256 /*amountOut*/,
        uint256 msgValue
    ) internal {
        // Read deltas
        int256 inDelta = FlashAccounting.getDelta(user, inputAsset);
        int256 outDelta = FlashAccounting.getDelta(user, outputAsset);

        // If both assets are the same token, merge deltas
        if (inputAsset == outputAsset) {
            int256 net = inDelta + outDelta; // could be positive (pay user) or negative (collect from user)
            if (net < 0) {
                // collect from user
                uint256 collect = uint256(-net);
                AssetTransferLib.transferIn(inputAsset, user, collect, inputAsset == address(0) ? msgValue : 0);
            } else if (net > 0) {
                // pay user
                uint256 pay = uint256(net);
                AssetTransferLib.transferOut(outputAsset, user, pay);
            }
            // clear
            FlashAccounting.clearDelta(user, inputAsset);
            return;
        }

        // Different tokens: settle separately
        if (inDelta < 0) {
            uint256 collectIn = uint256(-inDelta);
            AssetTransferLib.transferIn(inputAsset, user, collectIn, inputAsset == address(0) ? msgValue : 0);
        } else if (inDelta > 0) {
            // Should not happen in this flow; clear defensively by paying out
            AssetTransferLib.transferOut(inputAsset, user, uint256(inDelta));
        }

        if (outDelta > 0) {
            AssetTransferLib.transferOut(outputAsset, user, uint256(outDelta));
        } else if (outDelta < 0) {
            // Should not happen in this flow; collect from user
            AssetTransferLib.transferIn(outputAsset, user, uint256(-outDelta), outputAsset == address(0) ? msgValue : 0);
        }

        // clear
        FlashAccounting.clearDelta(user, inputAsset);
        FlashAccounting.clearDelta(user, outputAsset);
    }

    /// @notice Settle an arbitrary list of tokens for a user (session settle)
    /// @dev Positive delta => pay user; Negative delta => collect from user
    function settleUserTokens(
        address user,
        address[] memory tokens,
        uint256 msgValue
    ) internal {
        uint256 length = tokens.length;
        for (uint256 i = 0; i < length; ) {
            address token = tokens[i];
            int256 delta = FlashAccounting.getDelta(user, token);
            if (delta != 0) {
                if (delta > 0) {
                    AssetTransferLib.transferOut(token, user, uint256(delta));
                } else {
                    AssetTransferLib.transferIn(token, user, uint256(-delta), token == address(0) ? msgValue : 0);
                }
                FlashAccounting.clearDelta(user, token);
            }
            unchecked { ++i; }
        }
    }
}

contracts/Core/libraries/TransientStorag.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

library TransientStorage {
    /// @notice Store data in transient storage using tstore opcode
    /// @param key Address to use as storage key
    /// @param data Bytes data to store
    function storeTransient(address key, bytes memory data) internal {
        assembly {
            // Use address as the base slot directly for transient storage
            let slot := key
            // Store the data length first
            let dataLength := mload(data)
            tstore(slot, dataLength)
            // Store data in 32-byte chunks sequentially in transient storage
            let dataPtr := add(data, 0x20)
            let chunks := div(add(dataLength, 31), 32)
            for { let i := 0 } lt(i, chunks) { i := add(i, 1) } {
                let chunkSlot := add(slot, add(i, 1))
                let chunkData := mload(add(dataPtr, mul(i, 32)))
                tstore(chunkSlot, chunkData)
            }
        }
    }

    /// @notice Load data from transient storage using tload opcode
    /// @param key Address to use as storage key
    /// @return result Bytes data retrieved from transient storage
    function loadTransient(address key) internal view returns (bytes memory result) {
        assembly {
            let slot := key
            // Load the data length first
            let dataLength := tload(slot)
            // If no data, return empty bytes
            if iszero(dataLength) {
                result := mload(0x40)
                mstore(result, 0)
                mstore(0x40, add(result, 0x20))
            }
            // If data exists, load it
            if gt(dataLength, 0) {
                // Allocate memory for result
                result := mload(0x40)
                mstore(result, dataLength)
                let resultPtr := add(result, 0x20)
                // Load data in 32-byte chunks
                let chunks := div(add(dataLength, 31), 32)
                for { let i := 0 } lt(i, chunks) { i := add(i, 1) } {
                    let chunkSlot := add(slot, add(i, 1))
                    let chunkData := tload(chunkSlot)
                    mstore(add(resultPtr, mul(i, 32)), chunkData)
                }
                // Update free memory pointer
                mstore(0x40, add(result, add(0x20, mul(chunks, 32))))
            }
        }
    }
}

contracts/Core/PoolManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;
// ssssssss
// Custom errors for gas-efficient reverts
error PoolManager__InsufficientWithdrawal(uint256 amount0, uint256 amount1);
error PoolManager__InsufficientOutput(uint256 minOut, uint256 actual);
error PoolManager__InvalidQuote();
error PoolManager__Reentrancy();

import {ERC6909Claims} from "./ERC6909Claims.sol";
import {QuoteRouter} from "./QuoteRouter.sol";
import {PoolManagerLib} from "./libraries/PoolManagerLib.sol";
import {PoolIDAssembly} from "./libraries/PoolIDAssembly.sol";
import {SwapParams} from "./structs/SwapParams.sol";
import {Hop} from "./structs/Hop.sol";
import {PoolInfo} from "./structs/PoolInfo.sol";
import {ReentrancyGuard} from "./security/ReentrancyGuard.sol";
import {FlashAccounting} from "./libraries/FlashAccounting.sol";
import {IFlashCallback} from "./interfaces/internal/IFlashCallback.sol";

// Using library for clean storage access
using PoolManagerLib for PoolManagerLib.PoolManagerStorage;

contract PoolManager is ERC6909Claims, QuoteRouter, ReentrancyGuard {
    constructor(address _defaultMarketData, address _unused) QuoteRouter(_defaultMarketData, _unused) {}

    // Library storage for total liquidity tracking
    PoolManagerLib.PoolManagerStorage private _storage;



    // PoolID: 42955307580170980946467815337668002166680498660974576864971747189779899351040
    /// @notice Creates a new pool using library logic
    function createPool(
        address asset0,
        address asset1,
        address quoter,
        bytes3 markings
    ) external nonReentrant returns (uint256 poolID) {
        return PoolManagerLib.createPool(_storage, asset0, asset1, quoter, markings);
    }

    /// @notice Get pool information by poolID
    /// @param poolID Pool identifier
    /// @return asset0 First asset address (canonical order)
    /// @return asset1 Second asset address (canonical order)
    /// @return quoter Quoter contract address
    /// @return markings Pool configuration markings
    function getPoolInfo(uint256 poolID) external view returns (
        address asset0,
        address asset1,
        address quoter,
        bytes3 markings
    ) {
        PoolInfo memory poolInfo = PoolManagerLib.getPoolInfo(_storage, poolID);
        return (poolInfo.asset0, poolInfo.asset1, poolInfo.quoter, poolInfo.markings);
    }

    /// @notice Get total liquidity for a pool
    function totalLiquidity(uint256 poolID) external view returns (uint256) {
        return _storage.totalLiquidity[poolID];
    }

    /*//////////////////////////////////////////////////////////////
                            POOL INVENTORY MANAGEMENT
    //////////////////////////////////////////////////////////////*/

    /// @notice Get current pool inventory - PUBLIC INTERFACE
    function getInventory(uint256 poolId) external view returns (uint128 asset0, uint128 asset1) {
        return _getInventory(poolId);
    }

    // Internal helpers to avoid Remix parser issues with using-for dot calls
    function _getInventory(uint256 poolId) internal view returns (uint128 asset0, uint128 asset1) {
        uint256 packed = _storage.poolInventories[poolId];
        asset0 = uint128(packed);
        asset1 = uint128(packed >> 128);
    }

    function _updateInventory(
        uint256 poolId,
        int128 asset0Delta,
        int128 asset1Delta
    ) internal {
        uint256 packed = _storage.poolInventories[poolId];
        uint128 a0 = uint128(packed);
        uint128 a1 = uint128(packed >> 128);
        a0 = uint128(int128(a0) + asset0Delta);
        a1 = uint128(int128(a1) + asset1Delta);
        uint256 newPacked = uint256(a0) | (uint256(a1) << 128);
        if (newPacked != packed) {
            _storage.poolInventories[poolId] = newPacked;
        }
    }

    // Execution cost: 147,458..
    // Transactin cost:  170, 158
    /// @notice Add liquidity to a pool - SIMPLIFIED IMPLEMENTATION
    function addLiquidity(
        address asset0,
        address asset1,
        address quoter,
        bytes3 markings,
        uint256 amount0,
        uint256 amount1
    ) external payable nonReentrant returns (uint256 liquidity) {
        // Calculate poolID (canonicalizes asset order internally)
        uint256 poolID = PoolIDAssembly.assemblePoolID(asset0, asset1, quoter, markings);

        // Canonicalize amounts to match asset ordering used in poolID
        (address a0, address a1) = asset0 < asset1 ? (asset0, asset1) : (asset1, asset0);
        (uint256 amt0, uint256 amt1) = asset0 < asset1 ? (amount0, amount1) : (amount1, amount0);

        // Get current pool balances (canonical order)
        (uint128 poolAsset0, uint128 poolAsset1) = _getInventory(poolID);

        // Determine rate: if first liquidity, derive from provided amounts; otherwise, request via router
        // Define rate as asset0 per 1e18 units of asset1 (1e18 fixed point)
        uint256 rate;
        if (_storage.totalLiquidity[poolID] == 0) {
            require(amt0 > 0 && amt1 > 0, "Invalid initial amounts");
            rate = (amt0 * 1e18) / amt1;
        } else {
            SwapParams memory p = SwapParams({
                asset0: a0,
                asset1: a1,
                quoter: quoter,
                amount: new uint256[](1),
                zeroForOne: false, // quote asset1 -> asset0
                marking: new bytes3[](1)
            });
            p.amount[0] = 1e18; // 1 token (18 decimals)
            p.marking[0] = markings;
            (uint256 quoteAmount, ) = getQuote(p, poolAsset0, poolAsset1);
            if (quoteAmount == 0) revert PoolManager__InvalidQuote();
            rate = quoteAmount;
        }

        liquidity = PoolManagerLib.calculateLiquidityToMint(
            _storage, amt0, amt1, poolAsset0, poolAsset1, poolID, rate
        );

        // Determine beneficiary (session-aware originator)
        address beneficiary = FlashAccounting.getActiveUser();
        if (beneficiary == address(0)) beneficiary = msg.sender;

        // Flash accounting: record user owes tokens; defer actual transfer to settlement
        FlashAccounting.addDelta(beneficiary, a0, -int256(amt0));
        FlashAccounting.addDelta(beneficiary, a1, -int256(amt1));

        // Update inventory with canonical amounts
        _updateInventory(poolID, int128(uint128(amt0)), int128(uint128(amt1)));

        // Mint shares and update total directly
        _mint(beneficiary, poolID, liquidity);
        _storage.totalLiquidity[poolID] += liquidity;

        // If not in a session, settle immediately
        if (!FlashAccounting.isSessionActive(beneficiary)) {
            address[] memory tokens = new address[](2);
            tokens[0] = a0;
            tokens[1] = a1;
            PoolManagerLib.settleUserTokens(beneficiary, tokens, msg.value);
        }
    }

    /// @notice Remove liquidity from a pool - OPTIMAL IMPLEMENTATION
    function removeLiquidity(
        address asset0,
        address asset1,
        address quoter,
        bytes3 markings,
        uint256 liquidity
    ) external nonReentrant returns (uint256 amount0, uint256 amount1) {
        require(liquidity > 0, "Invalid liquidity amount");

        // Calculate poolID on-the-fly
        uint256 poolID = PoolIDAssembly.assemblePoolID(asset0, asset1, quoter, markings);

        // Get current pool balances
        (uint128 poolAsset0, uint128 poolAsset1) = _getInventory(poolID);
        require(_storage.totalLiquidity[poolID] > 0, "No liquidity in pool");

        // Calculate proportional amounts - simple math, inline
        amount0 = (liquidity * poolAsset0) / _storage.totalLiquidity[poolID];
        amount1 = (liquidity * poolAsset1) / _storage.totalLiquidity[poolID];
        if (amount0 == 0 && amount1 == 0) revert PoolManager__InsufficientWithdrawal(amount0, amount1);

        // Determine beneficiary (session-aware originator)
        address beneficiary = FlashAccounting.getActiveUser();
        if (beneficiary == address(0)) beneficiary = msg.sender;

        // Burn shares and update total directly
        _burn(beneficiary, poolID, liquidity);
        _storage.totalLiquidity[poolID] -= liquidity;

        // Update inventory via helper
        _updateInventory(poolID, -int128(uint128(amount0)), -int128(uint128(amount1)));

        // Flash accounting: record user is owed tokens; defer actual transfer to settlement
        bool canonicalOrder = asset0 < asset1;
        (uint256 out0, uint256 out1) = canonicalOrder ? (amount0, amount1) : (amount1, amount0);
        address a0 = canonicalOrder ? asset0 : asset1;
        address a1 = canonicalOrder ? asset1 : asset0;
        FlashAccounting.addDelta(beneficiary, a0, int256(out0));
        FlashAccounting.addDelta(beneficiary, a1, int256(out1));

        // If not in a session, settle immediately
        if (!FlashAccounting.isSessionActive(beneficiary)) {
            address[] memory tokens = new address[](2);
            tokens[0] = a0;
            tokens[1] = a1;
            PoolManagerLib.settleUserTokens(beneficiary, tokens, 0);
        }
    }

    // Execution (without market) cost: 67,670 
    // Execution cost (DualQuoter): 96,785
    // Transaction cost: 77,821
    /// @notice Swap assets in a pool - lean implementation
    function swap(
        address asset0,
        address asset1,
        address quoter,
        bytes3 markings,
        uint256 amountIn,
        bool zeroForOne,
        uint256 minAmountOut
    ) external payable nonReentrant returns (uint256 amountOut) {
        // Determine beneficiary (session-aware originator)
        address beneficiary = FlashAccounting.getActiveUser();
        if (beneficiary == address(0)) beneficiary = msg.sender;

        // Calculate poolID on-the-fly
        uint256 poolID = PoolIDAssembly.assemblePoolID(asset0, asset1, quoter, markings);

        // Get inventory and calculate output using quoter system
        (uint128 poolAsset0, uint128 poolAsset1) = _getInventory(poolID);


        // Create swap params for quoter
        SwapParams memory swapParams = SwapParams({
            asset0: asset0,
            asset1: asset1,
            quoter: quoter,
            amount: new uint256[](1),
            zeroForOne: zeroForOne,
            marking: new bytes3[](1)
        });
        swapParams.amount[0] = amountIn;
        swapParams.marking[0] = markings;

        // Restore external quoter now that transient storage replaced with sstore
        (uint256 quote, ) = getQuote(swapParams, poolAsset0, poolAsset1);
        if (quote == 0) revert PoolManager__InvalidQuote();
        amountOut = quote;


        // Validate and check minimums
        PoolManagerLib.validateSwapInventory(poolAsset0, poolAsset1, amountOut, zeroForOne);
        if (amountOut < minAmountOut) revert PoolManager__InsufficientOutput(minAmountOut, amountOut);


        // Update inventory - inline delta calculation
        // Update inventory in canonical asset order
        bool canonicalOrder = asset0 < asset1;
        bool canonicalZeroForOne = canonicalOrder ? zeroForOne : !zeroForOne;
        _updateInventory(
            poolID,
            canonicalZeroForOne ? int128(uint128(amountIn)) : -int128(uint128(amountOut)),
            canonicalZeroForOne ? -int128(uint128(amountOut)) : int128(uint128(amountIn))
        );

        // Flash accounting: record deltas for input and output; settle if not in session
        address inAsset = zeroForOne ? asset0 : asset1;
        address outAsset = zeroForOne ? asset1 : asset0;
        FlashAccounting.addDelta(beneficiary, inAsset, -int256(amountIn));
        FlashAccounting.addDelta(beneficiary, outAsset, int256(amountOut));

        if (!FlashAccounting.isSessionActive(beneficiary)) {
            PoolManagerLib._settleUserDeltas(beneficiary, inAsset, outAsset, 0, 0, msg.value);
        }

    }

    /// @notice Multi-hop batch swap within a single transaction. Outputs of each hop feed into the next.
    /// @dev Uses gas-optimized Hop structure with arrays for markings and amounts
    /// @param hops Array of hops, each containing arrays of markings and amounts
    /// @param amountIn Input amount for the first hop
    /// @param minAmountOut Minimum acceptable final output amount
    /// @return amountOut Final output amount after the last hop
    function batchSwap(
        Hop[] calldata hops,
        uint256 amountIn,
        uint256 minAmountOut
    ) external payable nonReentrant returns (uint256 amountOut) {
        // Determine effective recipient (session-aware originator)
        address recipient = FlashAccounting.getActiveUser();
        if (recipient == address(0)) recipient = msg.sender;

        amountOut = PoolManagerLib.executeBatchSwap(
            _storage,
            hops,
            amountIn,
            msg.value,
            recipient,
            address(this)
        );

        if (amountOut < minAmountOut) revert PoolManager__InsufficientOutput(minAmountOut, amountOut);
    }

    /// @notice Start a flash session to compose multiple operations with net settlement
    /// @param callback Address implementing IFlashCallback
    /// @param data Calldata forwarded to callback
    /// @param tokens List of tokens to settle at the end (scope what might be touched)
    function flashSession(address callback, bytes calldata data, address[] calldata tokens) external payable nonReentrantKey(keccak256("flashSession")) {
        // Mark session active and set active user context
        FlashAccounting.startSession(msg.sender);
        FlashAccounting.setActiveUser(msg.sender);

        // Execute user-provided logic
        IFlashCallback(callback).flashCallback(data);

        // Settle all touched tokens
        PoolManagerLib.settleUserTokens(msg.sender, tokens, msg.value);

        // End session and clear context
        FlashAccounting.clearActiveUser();
        FlashAccounting.endSession(msg.sender);
    }

    /// @notice Same as flashSession but returns per-token deltas for verification (should be zeros)
    function flashSessionWithReturn(address callback, bytes calldata data, address[] calldata tokens)
        external payable nonReentrantKey(keccak256("flashSession")) returns (int256[] memory deltas)
    {
        FlashAccounting.startSession(msg.sender);
        FlashAccounting.setActiveUser(msg.sender);
        IFlashCallback(callback).flashCallback(data);
        PoolManagerLib.settleUserTokens(msg.sender, tokens, msg.value);
        deltas = getUserDeltas(msg.sender, tokens);
        FlashAccounting.clearActiveUser();
        FlashAccounting.endSession(msg.sender);
    }

    /// @notice View helper to read current deltas for a user and tokens (transient storage)
    function getUserDeltas(address user, address[] calldata tokens) public view returns (int256[] memory deltas) {
        deltas = new int256[](tokens.length);
        for (uint256 i = 0; i < tokens.length; ) {
            deltas[i] = FlashAccounting.getDelta(user, tokens[i]);
            unchecked { ++i; }
        }
    }

    // Expose QuoterRouter's internal quote functions for library calls
    function routerGetQuote(
        SwapParams memory p,
        uint128 asset0Balance,
        uint128 asset1Balance
    ) public returns (uint256 quote, uint256 poolID) {
        return QuoteRouter.getQuote(p, asset0Balance, asset1Balance);
    }

    function routerGetQuoteBatch(
        SwapParams memory p,
        uint128[] memory asset0Balances,
        uint128[] memory asset1Balances
    ) public returns (uint256[] memory quote, uint256[] memory poolID) {
        return QuoteRouter.getQuoteBatch(p, asset0Balances, asset1Balances);
    }
}

contracts/Core/QuoteRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {MarkingHelper} from "./libraries/MarkingHelper.sol";
import {PoolIDAssembly} from "./libraries/PoolIDAssembly.sol";
import {TransientStorage} from "./libraries/TransientStorage.sol";

import {IQuoter} from "./interfaces/internal/quoters/IQuoter.sol";
import {IDataBridge} from "./interfaces/internal/IDataBridge.sol";

import {SwapParams} from "./structs/SwapParams.sol";
import {Marking} from "./structs/Marking.sol"; 
import {QuoteParams, QuoteParamsBatch} from "./structs/QuoteParams.sol";

abstract contract QuoteRouter {
    using TransientStorage for address;
    event MarketDataCache(address indexed provider, bool hit);
    address immutable public defaultMarketData;
    // Backward-compat exposure for tests
    address immutable public legacyDefaultBeta;
    mapping (uint16 => address) marketAddressStorage; // pointer space (0..2047 used)

    constructor(address _defaultMarketData, address _legacyDefaultBeta ) {
        defaultMarketData = _defaultMarketData;
        legacyDefaultBeta = _legacyDefaultBeta;
    }

    function addMarketAddress(uint16 pointer, address marketAddress) external {
        marketAddressStorage[pointer] = marketAddress;
    }

    function removeMarketAddress(uint16 pointer) external {
        delete marketAddressStorage[pointer];
    }

    function getMarketAddress(uint16 pointer) external view returns (address marketAddress) {
        marketAddress = marketAddressStorage[pointer];
    }

    function getMarketData(address dataAddress, QuoteParams memory params) internal returns (bytes memory data) {
        if (dataAddress == address(0)) {
            return bytes("");
        }
        // Skip non-contract addresses entirely
        if (dataAddress.code.length == 0) {
            return bytes("");
        }
        // Transient cache: check first
        data = dataAddress.loadTransient();
        bool hit = data.length > 0;
        if (!hit) {
            // Fetch and cache
            try IDataBridge(dataAddress).getData(params) returns (bytes memory fetched) {
                data = fetched;
                if (data.length > 0) {
                    dataAddress.storeTransient(data);
                }
            } catch {
                data = bytes("");
            }
        }
        emit MarketDataCache(dataAddress, hit);
        return data;
    }



    // Made public to allow library calls via PoolManager address
    function getQuote(SwapParams memory p, uint128 asset0Balance, uint128 asset1Balance) public returns (uint256 quote, uint256 poolID) {
        poolID = PoolIDAssembly.assemblePoolID(p.asset0, p.asset1, p.quoter, p.marking[0]);
        Marking memory m = MarkingHelper.decodeMarkings(p.marking[0]);

        QuoteParams memory params = QuoteParams({
            asset0: p.asset0,
            asset1: p.asset1,
            quoter: p.quoter,
            amount: p.amount[0],
            asset0Balance: asset0Balance,
            asset1Balance: asset1Balance,
            bucketID: m.bucketID,
            zeroForOne: p.zeroForOne
        });

        // No-data fast path: if marking indicates no market data needed
        if (!m.isMarket) {
            bytes memory empty = abi.encode(bytes(""), bytes(""));
            quote = IQuoter(params.quoter).quote(params, empty);
            return (quote, poolID);
        }

        // Single MarketData source. It can itself combine alpha/beta internally and return (alphaBytes,betaBytes)
        address market = m.isDefault ? defaultMarketData : marketAddressStorage[m.marketPtr];
        if (market == address(0)) { market = defaultMarketData; }
        bytes memory raw = getMarketData(market, params);
        bytes memory routed;
        if (raw.length == 0) {
            routed = abi.encode(bytes(""), bytes(""));
        } else {
            // If already (alphaBytes,betaBytes), forward; else wrap as (raw, "")
            try this._decodeTwoBytes(raw) returns (bytes memory a, bytes memory b) {
                routed = abi.encode(a, b);
            } catch {
                routed = abi.encode(raw, bytes(""));
            }
        }
        quote = IQuoter(params.quoter).quote(params, routed);
    }

    function getQuoteBatch(SwapParams memory p, uint128[] memory asset0Balances, uint128[] memory asset1Balances) public returns (uint256[] memory quote, uint256[] memory poolID) {
        poolID = new uint256[](p.marking.length);
        quote = new uint256[](p.marking.length);
        uint16[] memory bucketIDs = new uint16[](p.marking.length);
        Marking memory m = MarkingHelper.decodeMarkings(p.marking[0]);
        for (uint i = 0; i < p.marking.length; ) {
            bytes3 mark = p.marking[i];
            poolID[i] = PoolIDAssembly.assemblePoolID(p.asset0, p.asset1, p.quoter, mark);
            bucketIDs[i] = MarkingHelper.decodeMarkings(mark).bucketID;
            unchecked { ++i; }
        }
        QuoteParamsBatch memory params = QuoteParamsBatch({
            asset0: p.asset0,
            asset1: p.asset1,
            quoter: p.quoter,
            amount: p.amount,
            asset0Balances: asset0Balances,
            asset1Balances: asset1Balances,
            bucketID: bucketIDs,
            zeroForOne: p.zeroForOne
        });
        if (!m.isMarket) {
            bytes memory empty = abi.encode(bytes(""), bytes(""));
            quote = IQuoter(p.quoter).quoteBatch(params, empty);
            return (quote, poolID);
        }
        // For batch, fetch once per marking set
        QuoteParams memory baseParams = QuoteParams({
            asset0: p.asset0,
            asset1: p.asset1,
            quoter: p.quoter,
            amount: 0,
            asset0Balance: 0,
            asset1Balance: 0,
            bucketID: 0,
            zeroForOne: p.zeroForOne
        });
        address market = m.isDefault ? defaultMarketData : marketAddressStorage[m.marketPtr];
        if (market == address(0)) { market = defaultMarketData; }
        bytes memory raw = getMarketData(market, baseParams);
        bytes memory routed;
        if (raw.length == 0) {
            routed = abi.encode(bytes(""), bytes(""));
        } else {
            try this._decodeTwoBytes(raw) returns (bytes memory a, bytes memory b) {
                routed = abi.encode(a, b);
            } catch {
                routed = abi.encode(raw, bytes(""));
            }
        }
        quote = IQuoter(p.quoter).quoteBatch(params, routed);

    }

    // Helper to check if bytes is (bytes,bytes)
    function _decodeTwoBytes(bytes memory data) external pure returns (bytes memory a, bytes memory b) {
        (a, b) = abi.decode(data, (bytes, bytes));
    }
}

contracts/Core/security/ReentrancyGuard..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @title ReentrancyGuard
/// @notice Reentrancy guard implemented with EIP-1153 transient storage
abstract contract ReentrancyGuard {
    // Base domain separator for guard slots
    bytes32 private constant _REENTRANCY_GUARD_DOMAIN = keccak256("dex.reentrancy.guard:v1");

    modifier nonReentrant() {
        _enterReentrancyGuard(_globalGuardSlot());
        _;
        _exitReentrancyGuard(_globalGuardSlot());
    }

    modifier nonReentrantKey(bytes32 key) {
        bytes32 slot = _keyedGuardSlot(key);
        _enterReentrancyGuard(slot);
        _;
        _exitReentrancyGuard(slot);
    }

    function _globalGuardSlot() internal view returns (bytes32 slot) {
        slot = keccak256(abi.encodePacked(_REENTRANCY_GUARD_DOMAIN, address(this)));
    }

    function _keyedGuardSlot(bytes32 key) internal view returns (bytes32 slot) {
        slot = keccak256(abi.encodePacked(_REENTRANCY_GUARD_DOMAIN, address(this), key));
    }

    function _enterReentrancyGuard(bytes32 slot) private {
        assembly {
            if tload(slot) { revert(0, 0) }
            tstore(slot, 1)
        }
    }

    function _exitReentrancyGuard(bytes32 slot) private {
        assembly {
            tstore(slot, 0)
        }
    }
}

