// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {IDataBridge} from "../../Core/interfaces/internal/IDataBridge.sol";
import {QuoteParams} from "../../Core/structs/QuoteParams.sol";

/// @title MarketDataBridgeCombined
/// @notice Single market data bridge that aggregates Alpha and Beta data sources and returns a unified payload
/// @dev Returns abi.encode(alphaBytes, betaBytes). Alpha bytes can be arrays (spots, twaps) or (spot, twap) depending on the alpha source.
contract MarketDataBridgeCombined is IDataBridge {
    address public immutable alphaBridge; // e.g., AlphaDataBridgeOnchainAggregator
    address public immutable betaBridge;  // e.g., MockBetaDataBridge

    constructor(address _alphaBridge, address _betaBridge) {
        alphaBridge = _alphaBridge;
        betaBridge = _betaBridge;
    }

    function getData(QuoteParams memory params) external override returns (bytes memory) {
        bytes memory alphaBytes;
        bytes memory betaBytes;

        if (alphaBridge != address(0)) {
            alphaBytes = IDataBridge(alphaBridge).getData(params);
        }
        if (betaBridge != address(0)) {
            betaBytes = IDataBridge(betaBridge).getData(params);
        }
        return abi.encode(alphaBytes, betaBytes);
    }
}

