// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {IDataBridge} from "../../../Core/interfaces/internal/IDataBridge.sol";
import {QuoteParams} from "../../../Core/structs/QuoteParams.sol";
import {IChainlinkFeedRegistry} from "../../../Core/interfaces/external/IChainlinkFeedRegistry.sol";

/// @notice Chainlink-based bridge using Feed Registry to resolve USD feeds for assets
contract ChainlinkDataBridge is IDataBridge {
    address public immutable registry; // Chainlink Feed Registry
    address public constant USD = address(840); // sentinel for USD (per registry convention)

    constructor(address _registry) { registry = _registry; }

    // Returns abi.encode(spot1e18, updatedAtSeconds)
    function getData(QuoteParams memory params) external view override returns (bytes memory) {
        (uint256 p0, uint8 d0, uint256 u0) = _latestFromRegistry(params.asset0, USD);
        (uint256 p1, uint8 d1, uint256 u1) = _latestFromRegistry(params.asset1, USD);
        uint256 num = p0 * (10 ** (18 + d1));
        uint256 den = p1 * (10 ** d0);
        require(den != 0, "den=0");
        uint256 spot = num / den;
        uint256 updatedAt = u0 < u1 ? u0 : u1;
        return abi.encode(spot, updatedAt);
    }

    function _latestFromRegistry(address base, address quote) internal view returns (uint256, uint8, uint256) {
        IChainlinkFeedRegistry r = IChainlinkFeedRegistry(registry);
        ( , int256 answer, , uint256 updatedAt, ) = r.latestRoundData(base, quote);
        require(answer > 0, "bad answer");
        uint8 dec = r.decimals(base, quote);
        return (uint256(answer), dec, updatedAt);
    }
}
